ocks = snippet.$languageBlocks;\r\n        var $exampleBlock = snippet.$exampleBlock;\r\n        var idBase = \'tabbed-example-\' + idNum++;\r\n        var $wrapper = $WRAPPER.clone();\r\n        var $ul = $UL.clone();\r\n\r\n        for (var i = 0; i < languages.length; i++) {\r\n            var language = languages[i];\r\n            var $content = jQuery($languageBlocks[language]);\r\n            var id;\r\n            if ($content.attr(\'id\')) {\r\n                id = $content.attr(\'id\');\r\n            } else {\r\n                id = idBase + \'-\' + language;\r\n                $content.attr(\'id\', id);\r\n            }\r\n            $content.addClass(\'tab-pane\').css(\'position\', \'relative\');\r\n            var $li = $LI.clone();\r\n            var $a = $A.clone();\r\n\r\n            $a.attr(\'href\', \'#\' + id).text(LANGUAGES[language]).data(\'lang\', language).on(\'shown.bs.tab\', selectTab).on(\'click\', focusSelectedExample);\r\n\r\n            if (language in languageEventElements) {\r\n                languageEventElements[language].push($a);\r\n            } else {\r\n                languageEventElements[language] = [$a];\r\n            }\r\n            $wrapper.append($content);\r\n\r\n            if (storedLanguage) {\r\n                if (language === storedLanguage) {\r\n                    $li.addClass(\'active\');\r\n                    $content.addClass(\'active\');\r\n                }\r\n            } else if (i === 0) {\r\n                $li.addClass(\'active\');\r\n                $content.addClass(\'active\');\r\n            }\r\n\r\n            $li.append($a);\r\n            $ul.append($li);\r\n        }\r\n        $exampleBlock.children(\'div.example-title\', this).first().after($ul);\r\n        $exampleBlock.append($wrapper);\r\n    }\r\n}\r\n\r\nfunction storageAvailable(type) {\r\n    try {\r\n        var storage = window[type];\r\n        var x = \'__storage_test__\';\r\n        storage.setItem(x, x);\r\n        storage.removeItem(x);\r\n        return true;\r\n    }\r\n    catch(e) {\r\n        return false;\r\n    }\r\n}\r\n\r\nfunction getCodeExampleLanguage() {\r\n    return storageAvailable(\'sessionStorage\') ? sessionStorage.getItem(\'code_example_language\') || false : false;\r\n}</script>\r\n<script type=\"text/javascript\">jQuery(document).ready(function($){\r\nCodeMirror.colorize();\r\ntabTheSource(jQuery(\'#rcypher\'));\r\n});</script>','',1,29,'2017-01-01 09:00:00',156,'','2017-01-01 09:00:00',156,0,'0000-00-00 00:00:00','2017-06-02 15:08:40','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"article_layout\":\"_:default\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}',1,7,'','',1,0,'{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}',0,'*',''),(55,630,'Enhanced High Level Diagrams - Specs and Guidelines','icons-info','<h4>Making high-level-pathways illustrations interactive</h4>\r\n<h3>Introduction</h3>\r\n<p><span>The Enhanced High Level Diagrams (EHLD) project aims to improve the graphical representation of higher-level pathways in the Reactome events hierarchy, e.g., “signal transduction”, “apoptosis”, or “metabolism” whose pathway diagrams consist of green boxes labeled with the names of sub-events, optionally located in cellular compartments and connected by arrows. These green-box diagrams feature limited navigation: clicking on a green box takes the user to that sub-event. In some cases, an illustrator has also created a graphic representation of the event. However, these illustrator diagrams offer no navigation functionality.</span></p>\r\n<p><span>There was a general agreement that green-box diagrams are not that appealing, and put off users accustomed to textbook-quality illustrations of biological processes with striking, intuitively clear iconography. Meanwhile, while our illustrations often are up to textbook standards, they are static images. The project included generation of scalable vector graphic (SVG) versions of illustrations, and the development of software that will make these SVG images navigable in the way that the green-box figures are now, and also to provide user interactivity by progressive zoom in/out, actions when hovering over the items or selecting them and showing the associated content in the details panel for the selected items.</span></p>\r\n<p><span>This project has also driven the development of a consistent iconography, a controlled visual vocabulary for biological processes like the controlled word vocabularies we already have for names of physical entities and reactions.</span></p>\r\n<p><span>This document includes two main sections. The first section describes in detail the process that should be followed to create an EHLD. The second section of this document provides guide through the process of creating and including new graphic elements into the Reactome EHLD icons library. </span></p>\r\n<h3><span>Enhanced High Level Diagrams guidelines</span></h3>\r\n<p><span>This section aims to guide you through the process of creating an EHLD. The following paragraphs aim to summarise the set of requirements and guidelines the illustrator should follow in order to produce EHLDs that are compatible with Reactome Pathway Browser v3.4.</span></p>\r\n<h4><span>Generic guidelines</span></h4>\r\n<p><span>In Reactome, an EHLD is an interactive graphical representation of a higher level pathway diagram, typically containing two or more subpathways as active regions. An active region refers to a group of shapes representing a single subpathway. In particular, users can interact with active regions by hovering or selecting them, as well as use them to navigate to the subpathways they represent.</span></p>\r\n<p><span> &nbsp;</span><span><img src=\"images/ehldguide/ehld-with-analysis.png\" alt=\"ehld with analysis\" width=\"602\" height=\"386\" style=\"display: block; margin-left: auto; margin-right: auto;\" /></span></p>\r\n<p style=\"text-align: center;\"><em>EHLD representing the Hemostasis branch of the Reactome event hierarchy.&nbsp;(a) Pathway hierarchy view for Hemostasis. (b) Hemostasis as an EHLD representation. (c) Hemostasis EHLD overlaid with pathway enrichment analysis results.</em></p>\r\n<h3>Generic guidelines</h3>\r\n<p><span>To make all these possible, there is a set of initial requirements to be taken into account during the generation of the EHLDs.</span></p>\r\n<ul style=\"list-style-type: disc;\">\r\n<li><span>EHLDs must be in SVG format. Illustrators are free to use the design tool of their preference but at the end their diagram must be exported to SVG.</span></li>\r\n<li><span>The use of raster graphics is strongly discouraged as it results in larger file sizes and it has a negative impact on software performance. Also, by including bitmap images we cancel out resolution-independent zooming, which is one of SVG’s main advantage. &nbsp;</span></li>\r\n<li><span>All exported SVG files should include the styles in an internal CSS stylesheet. Please make use of this option when exporting from Adobe Illustrator: </span><i><span>Export As -&gt; SVG -&gt; Styling: Internal CSS</span></i><span>. If you are using another application to create the EHLDs, you will need to configure it appropriately.</span></li>\r\n<li><span>The name of each EHLD file has to be the Reactome Identifier of the corresponding pathway diagram, followed by “.svg”. For example, the </span><i><span>Apoptosis</span></i><span> EHLD should be named as </span><i><span>R-HSA-109581.svg</span></i><span>. Please keep in mind that only one EHLD should be created per high level pathway diagram.</span></li>\r\n<li><span>Active regions are annotated using the id attribute of their group element (inside the SVG file) and can be classified in the following two groups: </span>\r\n<ul style=\"list-style-type: circle;\">\r\n<li><span>Regions containing a group of irregular shapes (including text) that can be selected (and hovered) in order to navigate to the respective pathway diagram. These regions should be annotated by setting their id attribute to “</span><i><span>REGION-</span></i><span>” </span><i><span>+ Reactome Identifier</span></i><span> of their represented subpathway, e.g. “</span><i><span>REGION-R-HSA-109581</span></i><span>”. This type of active regions may include arrows pointing to or from them and they are not mandatory.</span></li>\r\n<li><span>Regions containing the label of the represented subpathway in a box-like shape that can be not only selected but also overlaid with the results of the analysis. These regions should be annotated by setting their id attribute to “</span><i><span>OVERLAY-</span></i><span>” </span><i><span>+ Reactome Identifier</span></i><span> of their represented subpathway, e.g. “</span><i><span>OVERLAY-R-HSA-109581</span></i><span>”. Regions of this category must not include arrows pointing to or from them, and they are mandatory.</span></li>\r\n<li><span>Please keep in mind that if we need to annotate both types of active regions for a given subpathway, the region (“REGION-R-HSA-109581”) has to include the overlay component (“OVERLAY-R-HSA-109581”). In other words, the group of the selectable shapes has to include the group of shapes that can be overlaid.</span></li>\r\n</ul>\r\n</li>\r\n<li><span>Any group of shapes, text or graphical elements that are not annotated as active regions are considered decorators and users cannot interact with them. They have pure esthetic value and they cannot be selected, hovered or overlaid. &nbsp;&nbsp;</span></li>\r\n<li><span>If possible, no background covering the whole image should be put in place. Please note that compartments or other necessary backgrounds can be kept, but we suggest to avoid “unnecessary” backgrounds. In case a background is required, but it is too big to fit in the illustration, it is suggested to draw only a small part of it with clear and well defined boundaries (See the blood vessel in the EHLD of Hemostasis above). </span></li>\r\n<li><span>Text elements should not be converted to groups of shapes. Instead, use the normal &lt;text&gt; SVG element.</span></li>\r\n</ul>\r\n<p><span><img src=\"images/ehldguide/subpathways.png\" alt=\"subpathways\" width=\"407\" height=\"272\" /></span></p>\r\n<p><span>Example illustrating the two types of active regions. Selectable regions (annotated with “REGION-” + Reactome Identifier) are highlighted in red, while regions that can be overlaid are highlighted in brown. Shapes and text that are not annotated as active regions are considered decorators (presented in blue) and users cannot interact with them.</span></p>\r\n<p></p>\r\n<p><span>Taking the aforementioned requirements into consideration, the EHLD of the figure above should have the following structure:</span></p>\r\n<p></p>\r\n<ul style=\"list-style-type: disc;\">\r\n<li><span>REGION-Pathway A </span><i><span># The whole pathway will be selectable #</span></i>\r\n<ul style=\"list-style-type: circle;\">\r\n<li><span>OVERLAY-Pathway A </span><i><span># Only the label will be overlaid #</span></i></li>\r\n</ul>\r\n</li>\r\n<li><span>REGION-Pathway B </span><i><span># The whole pathway will be selectable including the arrow #</span></i>\r\n<ul style=\"list-style-type: circle;\">\r\n<li><span>OVERLAY-Pathway B </span><i><span># Only the label will be overlaid #</span></i></li>\r\n</ul>\r\n</li>\r\n<li><span>REGION-Pathway C </span><i><span># The whole pathway will be selectable including the arrow #</span></i>\r\n<ul style=\"list-style-type: circle;\">\r\n<li><span>OVERLAY-Pathway C </span><i><span># Only the label will be overlaid #</span></i></li>\r\n</ul>\r\n</li>\r\n<li><span>OVERLAY-Pathway D &nbsp;</span><i><span>&nbsp;# The whole pathway will be selectable and overlaid #</span></i></li>\r\n<li><span>DECORATORS </span><i><span># None of the shapes are selectable or can be overlaid #</span></i></li>\r\n</ul>\r\n<h3>Creating an EHLD step by step</h3>\r\n<p><span>We suggest starting a new EHLD by creating an Adobe Illustrator document with </span><b>1366px</b><span> in width and </span><b>768px</b><span> in height and using the </span><b>RGB colour mode</b><span>.</span></p>\r\n<p><span>Pathway Labels should have </span><b>centered,</b> <b>white</b><span>, </span><b>uppercased</b><span>, text using </span><b>Arial Bold</b><span> font at </span><b>12pt</b><span> inside a </span><b>rounded rectangle</b><span> with </span><b>8pt</b> <b>radius</b><span>, minimum </span><b>width of 170px</b><span> and </span><b>height</b><span> of</span><b> 30px </b><span>for single lines or </span><b>43px</b><span> for double lines. The colour of the rounded rectangle should be </span><b>#0F82BC</b><span> (R:15 G:130 B:188). We suggest writing the name of the subpathway it represents as it appears in the Reactome hierarchy. In case of a discrepancy, or any kind of oddity, please contact the author or the curator related to the pathway.</span></p>\r\n<p><span>Please use the same specifications for any other written element except for:</span></p>\r\n<ul style=\"list-style-type: disc;\">\r\n<li><b>Descriptions</b><span> of a process should always be black and in lowercase letters.</span></li>\r\n<li><span>Any </span><b>written element</b><span> that belongs to a compound, protein or cell (generically inside of any coloured shape) should be white and in uppercase letters (when possible).</span></li>\r\n<li><span>Any </span><b>written element</b><span> that belongs to a receptor and/or describes a cell element or a process (generically outside of any shape) should be black and in lowercase letters (when possible, usually receptors are named by acronyms and capital letters should be used in these cases).</span></li>\r\n<li><span>Greek or other Unicode characters must be avoided in text elements. Thus, names with greek characters should include the whole word (e.g. alpha instead of α) written in lowercase.</span></li>\r\n<li><span>Reactome </span><b>logo</b><span> should always have </span><b>50%</b> <b>opacity</b><span>.</span></li>\r\n</ul>\r\n<p><span>Every pathway label should have one </span><b>Analysis Information Label</b><span>. The latter is used to display additional details about the hit elements and the false discovery rate (FDR). All analysis information labels should be annotated as “ANALINFO” in the Layer Hierarchy Panel. Place “XXX/YYY” as a text placeholder inside it. Text should be centered and uppercased in </span><b>Arial Bold</b> <b>9pt</b><span>, and in white colour. The box should be a rounded rectangle with </span><b>8px radius</b><span>,</span> <span>minimum </span><b>width of 170px</b><span> and </span><b>20px height</b><span>. Its fill colour should be </span><b>#C6C6C6</b><span> (R:198 G:198 B:198). Also, set the opacity of the group to 0%, but make sure you leave the opacity of the shapes and text inside the group to 100%. Each analysis information label should be placed beside its pathway label and in the opposite side of the content for that pathway. For instance, if the pathway label is above its content, the analysis information label should be placed above the pathway label and vice versa.</span></p>\r\n<p><span>In order to better shape the active region (or the clickable area) of a pathway we can add a white shape with </span><b>0% opacity</b><span> under any element of the Pathway in the Layer Hierarchy Panel. In this way we can extent the clickable area and improve the overall user experience while navigating the EHLDs. In particular, we should try to surround the elements of the subpathway covering the gaps within the individual graphic elements, and thus, avoiding the inconvenient situation where the mouse cursor changes from the usual arrow shape to the hand constantly. Additionally, in EHLDs where elements of a particular subpathway are too thin and users find it difficult to select them, this technique can provide a bigger and more convenient clickable area around them. However, we should use this with caution, as creating too large clickable areas can mislead users.</span></p>\r\n<p><span>It is recommended to place all arrows and neutral text elements in a group at the top of the layer hierarchy. As well, all decorators and elements in the background that we do not want to highlight or be clickable, we recommend to place them in a group at the bottom of the layer hierarchy.</span></p>\r\n<p><span>Reactome displays EHLDs in a blank zoomable space. For a better user experience we suggest to have all shapes and elements portrayed in full. However, we understand that some pathways are large and contain too many and too big elements to fit in one image. In cases where the illustrator needs to cut an element and it is difficult to make a clean representation of the cut, we suggest the addition of a gradient so that elements vanish keeping the limits of the canvas clear.</span></p>\r\n<p><span>In order to export an EHLD we should use the “Export as” option and save the file as an SVG with the following options:</span></p>\r\n<ul style=\"list-style-type: disc;\">\r\n<li><span>Styling: Internal CSS.</span></li>\r\n<li><span>Font: SVG.</span></li>\r\n<li><span>Images: Preserve.</span></li>\r\n<li><span>Object IDs: Layer Names.</span></li>\r\n<li><span>Decimal Points: 3.</span></li>\r\n<li><span>Minify and Responsive, both ticked.</span></li>\r\n</ul>\r\n<h3><span>The Reactome EHLD icons library guidelines</span></h3>\r\n<p><span>This section will guide you through the process of creating and including your own graphic elements into the Reactome Library and become a part of this growing community.</span></p>\r\n<p><span>Since Reactome is a free and open-source project, the Reactome Library is under a Creative Commons license. As a result, if your creations are included in our library, they will follow the same policy. Feel free to use any tool or application that allows you to create </span><b>vector graphics</b><span> and, please, make sure that the following specifications are implemented in the files you will be creating.</span></p>\r\n<h4><span>Generic guidelines</span></h4>\r\n<ul style=\"list-style-type: disc;\">\r\n<li><span>Every element should be placed into a separate file, </span><b>200</b><span> by </span><b>200px</b><span> in size, with </span><b>RGB colour mode</b><span> and </span><b>White background</b><span>.</span></li>\r\n<li><span>Use only </span><b>Arial Bold 12pt</b><span>. Text colour should be chosen depending on the text’s position. In case the text is </span><b>inside</b><span> a coloured shape, its colour should be </span><b>White</b><span>, while in case the text is </span><b>outside</b><span> of a coloured shape, its colour should be </span><b>Black</b><span>. </span></li>\r\n<li><span>All text should be </span><b>Uppercased</b><span> except for words describing </span><b>greek</b><span> characters (e.g alpha, beta, gamma, etc.). In that case those words should be written in </span><b>Lowercase</b><span>. It should be noted that Greek or other Unicode characters must be avoided, as they are not &nbsp;supported for the moment.</span></li>\r\n<li><span>Your elements should only comprise vector graphics. Please make sure that you have not included any bitmap image in it. Including raster images often results in larger file sizes and thus negatively impacts software performance. &nbsp;</span></li>\r\n<li><span>You can place your new element in any of the seven categories of the Reactome Library; Cell elements, Cell types, Compounds, Human tissue, Ion channels, Proteins and Receptors.</span></li>\r\n</ul>\r\n<h4><span>Cell elements</span></h4>\r\n<p><span>Keeping simplicity is one of our targets so we recommend the use of </span><b>simple figures</b><span>, such as circles, squares and triangles as a base. For colouring we suggest the use of shades of </span><b>Primary Colours</b><span> (Red, Blue and Yellow) and </span><b>Secondary Colours</b><span> (Purple, Green and Orange).</span></p>\r\n<p><span>Vector graphic applications allow you to use </span><b>Gradient tools</b><span> to blend two or more colours for the same shape. We recommend you to use these tools so you can create more appealing graphics. See the Mitochondrion below:</span></p>\r\n<p style=\"text-align: center;\"><span><img src=\"images/ehldguide/gradient_example.png\" alt=\"gradient example\" width=\"314\" height=\"232\" /></span></p>\r\n<p style=\"line-height: normal; text-align: center;\"><span style=\"font-size: 8pt;\"><em>(a) Without the use of gradients, the Mitochondrion element looks plain while in</em></span></p>\r\n<p style=\"line-height: normal; text-align: center;\"><span style=\"font-size: 8pt;\"><em>(b) through the correct use of gradients the element looks more 3D-like,&nbsp;</em></span></p>\r\n<p style=\"line-height: normal; text-align: center;\"><span style=\"font-size: 8pt;\"><em>resulting in a more attractive and playful image.</em></span></p>\r\n<h4><span>Cell types</span></h4>\r\n<p><span>In order to ensure a sense of unity and a consistent look and feel, we suggest applying the same principles; simple shapes and colours.</span></p>\r\n<p><span>For representing types of cells, please keep in mind that we are not using scale to portray the different organelles and elements inside the cell cytoplasm. Additionally, to maintain simplicity, try to avoid overloading the the overall design with too much detail. The following table features 3 examples: </span></p>\r\n<p></p>\r\n<table style=\"margin-left: auto; margin-right: auto;\">\r\n<tbody>\r\n<tr style=\"border: 1px solid #c9c9c9;\">\r\n<td style=\"border: 1px solid #c9c9c9;\"><br /><img src=\"images/ehldguide/microbe.png\" alt=\"microbe\" width=\"173\" height=\"173\" /></td>\r\n<td style=\"border: 1px solid #c9c9c9;\">\r\n<p style=\"text-align: justify;\"><span>This </span><b>Microbe</b><span> element is formed by a circle with a radial gradient of two colours. To represent the membrane, we have added an outline with a different gradient. To convey the idea of danger we use this squares as spikes and a red shade in their gradients.</span></p>\r\n</td>\r\n</tr>\r\n<tr style=\"border: 1px solid #c9c9c9;\">\r\n<td style=\"border: 1px solid #c9c9c9;\"><br /><img src=\"images/ehldguide/macrophage.png\" alt=\"macrophage\" width=\"154\" height=\"154\" /></td>\r\n<td style=\"border: 1px solid #c9c9c9;\">\r\n<p style=\"text-align: justify;\"><span>We have drawn this </span><b>Macrophage</b><span> element with shaky tentacles and we have used a not uniform outline (membrane) to give it a soft and flexible feeling.</span></p>\r\n</td>\r\n</tr>\r\n<tr style=\"border: 1px solid #c9c9c9;\">\r\n<td style=\"border: 1px solid #c9c9c9;\"><br /><img src=\"images/ehldguide/pathogen.png\" alt=\"pathogen\" width=\"166\" height=\"169\" /></td>\r\n<td style=\"border: 1px solid #c9c9c9; text-align: justify;\">\r\n<p><span>For this </span><b>Pathogen</b><span> element we used a large light gradient to represent a shiny and hard capsule.</span></p>\r\n<p><span>To show its death we draw holes on its surface and play with a darkened, sad colour.</span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h4 style=\"text-align: justify;\"><span>Compounds</span></h4>\r\n<p style=\"text-align: justify;\"><span>Compounds are represented by very geometrical elements, following more specific guidelines.</span></p>\r\n<p style=\"text-align: justify;\"><span>Simple chemical elements or compounds are usually portrayed in the Library with their chemical element symbol (e.g. Ca for Calcium) or the name of the compound (e.g. IFN-gamma) using </span><b>Arial bold 12pt</b><span> in </span><b>White</b><span> colour.</span></p>\r\n<p style=\"text-align: justify;\"><span>Simple chemical elements are represented by an </span><b>octagon</b><span> of </span><b>28px</b><span> height and two plain colours, one for the fill and a different one for the stroke (</span><b>2pt</b><span>). In case the text of the compound is too long to fit in the octagon, we suggest stretching the shape until it fits the name, respecting its lateral edges, like follows:</span></p>\r\n<p style=\"text-align: center;\"><span><img src=\"images/ehldguide/h2o.png\" alt=\"h2o\" width=\"183\" height=\"138\" /></span></p>\r\n<p style=\"text-align: center;\"><em>An example of a simple chemical element.</em></p>\r\n<p></p>\r\n<p style=\"text-align: justify;\"><em></em>Complex compounds can be represented with a variety of shapes, always simple and easy to differentiate one from each other.</p>\r\n<p style=\"text-align: center;\"></p>\r\n<table style=\"margin-left: auto; margin-right: auto;\">\r\n<tbody>\r\n<tr style=\"border: 1px solid #c9c9c9;\">\r\n<td style=\"border: 1px solid #c9c9c9;\"><br /><img src=\"images/ehldguide/atp.png\" alt=\"atp\" width=\"123\" height=\"120\" /></td>\r\n<td style=\"border: 1px solid #c9c9c9;\">\r\n<p><span>We can use circles with or without outline.</span></p>\r\n</td>\r\n</tr>\r\n<tr style=\"border: 1px solid #c9c9c9;\">\r\n<td style=\"border: 1px solid #c9c9c9;\"><br /><img src=\"images/ehldguide/alpha_toh.png\" alt=\"alpha toh\" width=\"124\" height=\"77\" /></td>\r\n<td style=\"border: 1px solid #c9c9c9;\">\r\n<p><span>We can use hexagons, in the same way as for the simple compounds, just adding a gradient on their stroke.</span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: center;\"></p>\r\n<h4 style=\"text-align: justify;\"><span>Human tissue</span></h4>\r\n<p style=\"text-align: justify;\"><span>Human tissue elements are mainly used as backgrounds in our diagrams and the way of representing these organs depends on the illustrator’s skills and taste. To ensure unity in the Reactome Library we suggest to keep these illustrations as simple as possible and visualise them as toys; so they convey a nice, soft feeling, with a plastic-like, very friendly and approachable look.</span></p>\r\n<p style=\"text-align: center;\"></p>\r\n<table style=\"background-position: center center; margin-left: auto; margin-right: auto;\">\r\n<tbody>\r\n<tr style=\"border-image: initial; border: 1px solid #c9c9c9;\">\r\n<td style=\"border-image: initial; border: 1px solid #c9c9c9;\">\r\n<p style=\"text-align: center;\"><span><img src=\"images/ehldguide/liver.png\" alt=\"liver\" width=\"190\" height=\"114\" /></span></p>\r\n</td>\r\n<td style=\"border-image: initial; border: 1px solid #c9c9c9;\">\r\n<p><span><img src=\"images/ehldguide/blood_vessel.png\" alt=\"blood vessel\" width=\"260\" height=\"117\" /></span></p>\r\n</td>\r\n</tr>\r\n<tr style=\"border-image: initial; border: 1px solid #c9c9c9;\">\r\n<td colspan=\"2\" style=\"border-image: initial; border: 1px solid #c9c9c9;\">\r\n<p style=\"text-align: center;\"><em>Examples of human tissue elements; Liver and Blood vessel.</em></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h4 style=\"text-align: justify;\"><span>Ion channels</span></h4>\r\n<p style=\"text-align: justify;\"><span>These representations are quite simplified and, thus, elements are designed to look like small funnels that cross through the membrane, allowing elements to move from inside of the cell to outside and vice versa. There are hundreds of different ion channels and we suggest differentiating them with the use of colours in various combinations.</span></p>\r\n<p style=\"text-align: center;\"></p>\r\n<table style=\"margin-left: auto; margin-right: auto;\">\r\n<tbody>\r\n<tr style=\"border: 1px solid #c9c9c9;\">\r\n<td style=\"border: 1px solid #c9c9c9;\">\r\n<p><span><img src=\"images/ehldguide/calcium_channel.png\" alt=\"calcium channel\" width=\"150\" height=\"192\" /></span></p>\r\n</td>\r\n<td style=\"border: 1px solid #c9c9c9;\">\r\n<p><span><img src=\"images/ehldguide/proton_channel.png\" alt=\"proton channel\" width=\"159\" height=\"186\" /></span></p>\r\n</td>\r\n</tr>\r\n<tr style=\"border: 1px solid #c9c9c9;\">\r\n<td colspan=\"2\" style=\"border: 1px solid #c9c9c9;\">\r\n<p style=\"text-align: center;\"><em>Examples of ion channels.</em></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h4 style=\"text-align: justify;\"><span>Proteins</span></h4>\r\n<p style=\"text-align: justify;\"><span>There are tens of thousands of different proteins, some of them with specific shapes. Proteins in the Reactome Library follow the standard representation as rounded rectangle shapes. As a result, we suggest using the </span><b>Rounded Rectangle Tool</b><span> to draw a shape with </span><b>10px</b><span> radius for the corners and </span><b>20px</b><span> height for single line of text, or </span><b>30px</b><span> for a double line. The stroke should be </span><b>3pt </b><span>and we suggest choosing an irregular profile (in Adobe Illustrator, use the default </span><b>Width Profile 2</b><span>). For the name of the proteins we suggest to follow the same guides as before, </span><b>White Uppercase Arial Bold 12pt</b><span>.</span></p>\r\n<p style=\"text-align: justify;\"><span>The shape will contain the simplified name of the protein following the general instructions for Reactome Library. It should be filled with a simple gradient of two colours. One of the &nbsp;colours of this gradient should also be used as the stroke colour.</span></p>\r\n<p style=\"text-align: center;\"></p>\r\n<table style=\"margin-left: auto; margin-right: auto;\">\r\n<tbody>\r\n<tr style=\"border: 1px solid #c9c9c9;\">\r\n<td style=\"border: 1px solid #c9c9c9;\">\r\n<p><span><img src=\"images/ehldguide/plc_beta1.png\" alt=\"plc beta1\" width=\"195\" height=\"80\" /></span></p>\r\n</td>\r\n<td style=\"border: 1px solid #c9c9c9;\">\r\n<p><span><img src=\"images/ehldguide/plasminogen.png\" alt=\"plasminogen\" width=\"285\" height=\"83\" /></span></p>\r\n</td>\r\n</tr>\r\n<tr style=\"border: 1px solid #c9c9c9;\">\r\n<td colspan=\"2\" style=\"border: 1px solid #c9c9c9;\">\r\n<p style=\"text-align: center;\"><em>Examples of protein elements in their rounded rectangle representation.</em></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p></p>\r\n<p style=\"text-align: justify;\"><span>In case a protein has a specific shape, we suggest keeping the standard representation as long as it is not too complicated. Otherwise, we suggest opting for a more simplified version.</span></p>\r\n<p style=\"text-align: center;\"></p>\r\n<table style=\"margin-left: auto; margin-right: auto;\">\r\n<tbody>\r\n<tr style=\"border: 1px solid #c9c9c9;\">\r\n<td style=\"border: 1px solid #c9c9c9;\">\r\n<p><span><img src=\"images/ehldguide/mac.png\" alt=\"mac\" width=\"160\" height=\"164\" /></span></p>\r\n</td>\r\n<td style=\"border: 1px solid #c9c9c9;\">\r\n<p><span><img src=\"images/ehldguide/antibody.png\" alt=\"antibody\" width=\"189\" height=\"167\" /></span></p>\r\n</td>\r\n</tr>\r\n<tr style=\"border: 1px solid #c9c9c9;\">\r\n<td colspan=\"2\" style=\"border: 1px solid #c9c9c9;\">\r\n<p style=\"text-align: center;\"><em>Examples of protein elements represented in their standard representation.</em></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h4 style=\"text-align: justify;\"><span>Receptors</span></h4>\r\n<p style=\"text-align: justify;\"><span>Like proteins, there is a huge number of receptors. In cases where there is a standard, but quite complex, way to represent them, we suggest adopting a more simplified version. If this is too difficult we suggest finding a more simple representation.</span></p>\r\n<p style=\"text-align: center;\"></p>\r\n<table style=\"margin-left: auto; margin-right: auto;\">\r\n<tbody>\r\n<tr style=\"border: 1px solid #c9c9c9;\">\r\n<td style=\"border: 1px solid #c9c9c9;\">\r\n<p><span><img src=\"images/ehldguide/fc_gamma_r.png\" alt=\"FCgammaR\" width=\"111\" height=\"209\" style=\"float: left;\" /></span></p>\r\n</td>\r\n<td style=\"border: 1px solid #c9c9c9;\">\r\n<p><span><img src=\"images/ehldguide/cd40-cd40l.png\" alt=\"cd40-cd40l\" width=\"97\" height=\"196\" style=\"float: right;\" /></span></p>\r\n</td>\r\n</tr>\r\n<tr style=\"border: 1px solid #c9c9c9;\">\r\n<td colspan=\"2\" style=\"border: 1px solid #c9c9c9;\">\r\n<p style=\"text-align: center;\"><em>Examples of receptor elements</em></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h4 style=\"text-align: justify;\"><span>Metadata</span></h4>\r\n<p style=\"text-align: justify;\"><span>You are encouraged to accompany your element with more information including a short description and extra details about its designer and curator. Upon inclusion of your element into the Reactome Library, all metadata information will be available to the public through our landing page.</span></p>\r\n<p style=\"text-align: justify;\"><span>In order to do so, you need to create an additional metadata file using your favorite text editor. The file should have the following structure:</span></p>\r\n<p style=\"text-align: justify;\"></p>\r\n<p style=\"text-align: justify; padding-left: 30px;\"><span style=\"font-family: \'courier new\', courier;\">&lt;metadata&gt;<br /> &nbsp;&nbsp;&nbsp;&lt;person role=\"curator\" orcid=\"<b>{ORCID}</b>\" url=\"<b>{URL}</b>\"&gt;<b>{C_NAME}</b>&lt;/person&gt;<br /> &nbsp;&nbsp;&nbsp;&lt;person role=\"designer\" orcid=\"<b>{ORCID}</b>\" url=\"<b>{URL}</b>\"&gt;<b>{D_NAME}</b>&lt;/person&gt;<br /> &nbsp;&nbsp;&nbsp;&lt;description&gt;<b>{DESCRIPTION}</b>&lt;/description&gt;<br />&lt;/metadata&gt;</span></p>\r\n<p style=\"text-align: justify;\"><span>Please follow these steps: </span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span>Create a new file using your favorite text editor (TextEdit, NotePad, etc.).</span></li>\r\n<li><span>Copy and Paste the text above into your file.</span></li>\r\n<li><span>Edit the file and:</span><ol>\r\n<li><span>Fill {ORCID} and/or {URL} for the curator and designer roles. This is </span><a href=\"https://orcid.org/orcid-search/search\"><span>how to find ORCID</span></a><span>. In case both URL and ORCID are provided, the link for that person will point to the URL.</span></li>\r\n<li><span>Fill {C_NAME} and {D_NAME} as the curator and designer name, respectively.</span></li>\r\n<li><span>Fill {DESCRIPTION}. &nbsp;</span></li>\r\n</ol></li>\r\n<li><span>Save the file using the same name as the element but make sure to use .xml as its extension. For example if your graphic element is stored with the name “Element.svg” then your metadata file should be named as “Element.xml”.</span></li>\r\n<li><span>Make sure to submit your metadata file along with the graphics file.</span></li>\r\n</ol>\r\n<h3 style=\"text-align: justify;\"><span>Export Settings</span></h3>\r\n<p style=\"text-align: justify;\"><span>Currently, every element in the Reactome Library is available in 3 different formats, SVG, EMF and PNG. Therefore, you are kindly requested to export your work into these formats and include them in your submission.</span></p>\r\n<h3 style=\"text-align: justify;\"><span>How to submit your work</span></h3>\r\n<p style=\"text-align: justify;\"><span>To include your graphic elements in the Reactome Library, you need to contact our helpdesk (</span><a href=\"mailto:help@reactome.org\"><span>help@reactome.org</span></a><span>). We will then review your submitted files and, depending on the outcome, we will include them in our Library.</span></p>','',1,14,'2017-01-01 09:00:00',156,'','2017-01-01 09:00:00',156,0,'0000-00-00 00:00:00','2017-06-05 15:23:33','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}',1,2,'','',1,0,'{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}',0,'*',''),(56,631,'Pathways Overview JS','pathways-overview-js','<style type=\"text/css\">pre {\r\n    word-wrap: initial;\r\n    white-space: pre;\r\n    word-break: break-all;\r\n}\r\n}</style>\r\n<div>Reactome\'s Fireworks JS widget is our pathways overview viewer in an ordinary JavaScript API. It is meant to be reused by third party resources in order to display Reactome pathways overview directly in their web pages and enable users to interact with them.</div>\r\n<p>&nbsp;</p>\r\n<div id=\"fireworksHolder\" class=\"diagram\"></div>\r\n<div>\r\n<h3><a href=\"#reuse-pathways-widget\" id=\"reuse-pathways-widget\"><i class=\"fa fa-rocket\" style=\"font-size: 23px;\"></i>Reusing Reactome\'s Pathways Overview Widget?</a></h3>\r\n<p>To reuse our viewer you need to follow the following steps</p>\r\n<p>1. Include the fireworks javascript dependency in you HTML header</p>\r\n<pre class=\"code\">    &lt;script type=\"text/javascript\" language=\"javascript\" src=\"https://reactome.org/FireworksJs/fireworks/fireworks.nocache.js\"&gt;&lt;/script&gt;\r\n        </pre>\r\n<p>2. Add a place holder in the body of your web page</p>\r\n<pre class=\"prettyprint code\">    &lt;div id=\"fireworksHolder\"&gt;&lt;/div&gt;\r\n        </pre>\r\n<p>3. Set a proxy in your server under \"/reactome\" pointing to \"https://reactome.org\"</p>\r\n<p>4. Create and initialise the pathways overview from your javascript code</p>\r\n<pre class=\"prettyprint code\">    //Creating the Reactome pathways overview widget\r\n    function onReactomeFireworksReady(){  //This function is automatically called when the widget code is ready to be used\r\n        var fireworks = Reactome.Fireworks.create({\r\n            \"placeHolder\" : \"fireworksHolder\",\r\n            \"width\" : 930,\r\n            \"height\" : 500\r\n        });\r\n\r\n        //Adding different listeners\r\n\r\n        fireworks.onFireworksLoaded(function (loaded) {\r\n            console.info(\"Loaded \", loaded);\r\n        });\r\n\r\n        fireworks.onNodeHovered(function (hovered){\r\n            console.info(\"Hovered \", hovered);\r\n        });\r\n\r\n        fireworks.onNodeSelected(function (selected){\r\n            console.info(\"Selected \", selected);\r\n        });\r\n    }\r\n        </pre>\r\n<a>&nbsp;</a>\r\n<h3><a href=\"#API\" id=\"API\"><i class=\"fa fa-code\" style=\"font-size: 23px;\"></i>FireworksJs API</a></h3>\r\n<p>The current implementation supports the following listeners and methods:</p>\r\n<div id=\"r-responsive-table\">\r\n<table class=\"reactome\">\r\n<thead>\r\n<tr><th>Method</th><th>Params</th><th>Description</th></tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td data-label=\"Method\"><b>create</b> :: Constructor<br /> &nbsp;&nbsp;Reactome.Fireworks.create(params);</td>\r\n<td data-label=\"Params\"><b>param</b> :: json object<br /> &nbsp;{<br /> &nbsp;&nbsp;&nbsp;\'proxyPrefix\' : string,<br /> &nbsp;&nbsp;&nbsp;\'placeHolder\' : string,<br /> &nbsp;&nbsp;&nbsp;\'width\' : int (optional),<br /> &nbsp;&nbsp;&nbsp;\'height\' : int (optional)<br /> &nbsp;}</td>\r\n<td data-label=\"Description\">Creates and returns a new Reactome.Fireworks object</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>flagItems(String identifier)</b> :: void</td>\r\n<td data-label=\"Params\">Item identifier <br /> <b>identifier</b> : string</td>\r\n<td data-label=\"Description\">Flags pathways where the identifier is found. It accepts main identifiers but also cross references, gene names and physical entity stable identifiers</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>highlightNode(stId)</b> :: void</td>\r\n<td data-label=\"Params\">Item stable identifier<br /> <b>stId</b> : string</td>\r\n<td data-label=\"Description\">Highlights the specified item if it exists in the fireworks</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>resetAnalysis()</b> :: void</td>\r\n<td data-label=\"Params\">&nbsp;</td>\r\n<td data-label=\"Description\">Resets the analysis overlay</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>resetHighlight()</b> :: void</td>\r\n<td data-label=\"Params\">&nbsp;</td>\r\n<td data-label=\"Description\">Clears the highlights in the fireworks</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>resetSelection()</b> :: void</td>\r\n<td data-label=\"Params\">&nbsp;</td>\r\n<td data-label=\"Description\">Clears the selection in the fireworks</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>resize(width, height)</b> :: void</td>\r\n<td data-label=\"Params\"><b>width</b>: int<br /> <b>height</b>: int</td>\r\n<td data-label=\"Description\">Resizes the viewport to the specified with and height</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>selectNode(stId)</b> :: void</td>\r\n<td data-label=\"Params\">Item stable identifier<br /> <b>stId</b> : string</td>\r\n<td data-label=\"Description\">Selects the specified item if it exists in the fireworks</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>setAnalysisToken(token, resource)</b> :: void</td>\r\n<td data-label=\"Params\">Analysis token<br /> <b>token</b> : string<br /> Resource<br /> <b>resource</b> : string</td>\r\n<td data-label=\"Description\">Overlays the analysis result corresponding to the specified (token, resource)</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>showAll()</b> :: void</td>\r\n<td data-label=\"Params\">&nbsp;</td>\r\n<td data-label=\"Description\">Shows all the pathways in the viewport</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onNodesFlaggedReset(function())</b> :: void</td>\r\n<td data-label=\"Params\">The function receives no parameters</td>\r\n<td data-label=\"Description\">The function is called when the user resets the flagged items</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onNodeSelected(function(obj))</b> :: void</td>\r\n<td data-label=\"Params\"><b>obj</b> is selected item:<br /> &nbsp;{<br /> &nbsp;&nbsp;&nbsp;\'stId\' : string,<br /> &nbsp;&nbsp;&nbsp;\'displayName\' : string,<br /> &nbsp;&nbsp;&nbsp;\'schemaClass\' : string,<br /> &nbsp;}</td>\r\n<td data-label=\"Description\">The function is called when an object in the fireworks is selected by the user action</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onNodeHovered(function(obj))</b> :: void</td>\r\n<td data-label=\"Params\"><b>obj</b> is hovered item:<br /> &nbsp;{<br /> &nbsp;&nbsp;&nbsp;\'stId\' : string,<br /> &nbsp;&nbsp;&nbsp;\'displayName\' : string,<br /> &nbsp;&nbsp;&nbsp;\'schemaClass\' : string,<br /> &nbsp;}</td>\r\n<td data-label=\"Description\">The function is called when an object in the fireworks is hovered by the user action</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onFireworksLoaded(function(id))</b> :: void</td>\r\n<td data-label=\"Params\">The db identifier of the loaded species in fireworks<br /> <b>id</b>: string</td>\r\n<td data-label=\"Description\">The function is called when a fireworks is loaded in the viewer</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onAnalysisReset(function())</b> :: void</td>\r\n<td data-label=\"Params\">The function receives no parameters</td>\r\n<td data-label=\"Description\">The function is called when the users resets the analysis overlay<br /><br /> fireworks.onAnalysisReset(function(){ /* your code here */ });</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onCanvasNotSupported(function())</b> :: void</td>\r\n<td data-label=\"Params\">The function receives no parameters.</td>\r\n<td data-label=\"Description\">The function is called when the browser doesn\'t support HTML5 Canvas so the viewer cannot be instantiated<br /><br /> fireworks.onCanvasNotSupported(function(){ /* your code here */ });</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<script type=\"text/javascript\">function getMainContentWidth(){\r\n   var DIV = \"fav-maincontent\";\r\n   var style = window.getComputedStyle(document.getElementById(DIV), null);\r\n   var width = style.getPropertyValue(\"width\").replace(\"px\",\"\");\r\n   var paddingLeft = style.getPropertyValue(\"padding-left\").replace(\"px\",\"\"); \r\n   var paddingRight = style.getPropertyValue(\"padding-right\").replace(\"px\",\"\");\r\n   return width - paddingLeft - paddingRight;\r\n}\r\n\r\njQuery( window ).resize(function() {\r\n  fireworks.resize(getMainContentWidth(), 600);  \r\n});\r\n\r\nfunction onReactomeFireworksReady() {\r\n            fireworks = Reactome.Fireworks.create({\r\n                \"placeHolder\": \"fireworksHolder\",\r\n                \"width\": getMainContentWidth(),\r\n                \"height\": 600\r\n            });\r\n            fireworks.onFireworksLoaded(function (loaded) {\r\n                console.info(\"Loaded \", loaded);\r\n            });\r\n            fireworks.onNodeHovered(function (hovered) {\r\n                console.info(\"Hovered \", hovered);\r\n            });\r\n            fireworks.onNodeSelected(function (selected) {\r\n                console.info(\"Selected \", selected);\r\n            });\r\n        }</script>\r\n<script src=\"/FireworksJs/fireworks/fireworks.nocache.js\" type=\"text/javascript\" language=\"javascript\"></script>\r\n</div>','',1,29,'2017-01-01 09:00:00',156,'','2017-01-01 09:00:00',156,0,'0000-00-00 00:00:00','2017-06-07 10:10:59','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}',1,6,'','',1,0,'{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}',0,'*',''),(57,632,'Pathways Overview GWT','pathways-overview-gwt','<p>Reactome’s Fireworks GWT widget is our original implementation of the pathways overview in GWT. It is meant to be reused by third party resources in order to display Reactome pathways overview directly in their web pages and enable users to interact with them.</p>\r\n<p>{loadposition dev-guide-po-gwt-1}</p>\r\n<div>\r\n<h3><a href=\"#MoreInfo\" id=\"MoreInfo\"><i class=\"fa fa-question-circle\" style=\"font-size: 23px;\"></i>More Info</a></h3>\r\n<p><a href=\"http://www.gwtproject.org/\">GWT</a>&nbsp;is a development toolkit for building and optimizing complex browser-based applications. Its goal is to enable productive development of high-performance web applications without the developer having to be an expert in browser quirks, XMLHttpRequest, and JavaScript. GWT is used by many products at Google, including AdWords, AdSense, Flights, Hotel Finder, Offers, Wallet, Blogger. It’s open source, completely free, and used by thousands of developers around the world.</p>\r\n<p>&nbsp;</p>\r\n<h3><a href=\"#GetStarted\" id=\"GetStarted\"><i class=\"fa fa-rocket\" style=\"font-size: 23px;\"></i>Get Started</a></h3>\r\n<p>We assume that you already have your <a href=\"http://www.gwtproject.org/gettingstarted.html\">GWT project</a> up and running configured with the <a href=\"http://www.gwtproject.org/doc/latest/RefCommandLineTools.html\"> MAVEN archetype</a>. So the first step is to add the EBI Nexus repository in your pom.xml file:</p>\r\n<pre><code>&lt;repositories&gt;\r\n    ...\r\n    &lt;!-- EBI repo --&gt;\r\n    &lt;repository&gt;\r\n        &lt;id&gt;nexus-ebi-repo&lt;/id&gt;\r\n        &lt;name&gt;The EBI internal repository&lt;/name&gt;\r\n        &lt;url&gt;http://www.ebi.ac.uk/Tools/maven/repos/content/groups/ebi-repo/&lt;/url&gt;\r\n        &lt;releases&gt;\r\n            &lt;enabled&gt;true&lt;/enabled&gt;\r\n        &lt;/releases&gt;\r\n        &lt;snapshots&gt;\r\n            &lt;enabled&gt;false&lt;/enabled&gt;\r\n        &lt;/snapshots&gt;\r\n    &lt;/repository&gt;\r\n    &lt;!-- EBI SNAPSHOT repo --&gt;\r\n    &lt;repository&gt;\r\n        &lt;id&gt;nexus-ebi-snapshot-repo&lt;/id&gt;\r\n        &lt;name&gt;The EBI internal snapshot repository&lt;/name&gt;\r\n        &lt;url&gt;http://www.ebi.ac.uk/Tools/maven/repos/content/groups/ebi-snapshots/&lt;/url&gt;\r\n        &lt;releases&gt;\r\n            &lt;enabled&gt;false&lt;/enabled&gt;\r\n        &lt;/releases&gt;\r\n        &lt;snapshots&gt;\r\n            &lt;enabled&gt;true&lt;/enabled&gt;\r\n        &lt;/snapshots&gt;\r\n    &lt;/repository&gt;\r\n&lt;/repositories&gt;\r\n</code></pre>\r\n<p>Once the repository is in place, the next thing to do is adding the Reactome\'s pathways overview dependency:</p>\r\n<pre><code>&lt;dependencies&gt;\r\n    ...\r\n    &lt;dependency&gt;\r\n        &lt;groupId&gt;org.reactome.web&lt;/groupId&gt;\r\n        &lt;artifactId&gt;fireworks&lt;/artifactId&gt;\r\n        &lt;version&gt;1.0.0&lt;/version&gt;\r\n    &lt;/dependency&gt;\r\n&lt;dependencies&gt;\r\n</code></pre>\r\n<p>Before writing the code to use the pathways overview, add the dependency in your <b>gwt.xml</b> file, so the compiler will find the pathways overview:</p>\r\n<pre><code>&lt;inherits name=\"org.reactome.web.fireworks.FireworksViewer\" /&gt;\r\n</code></pre>\r\n<p>And finally, assuming the widget is now going to be displayed by its own, the code in your <b>EntryPoint</b> class would look like:</p>\r\n<pre><code>/**\r\n * For test purposes we assume the pathway overview loads as a single item in the web page.\r\n *\r\n * Entry point classes define onModuleLoad().\r\n */\r\npublic class Main implements EntryPoint {\r\n\r\n    private final FirewowksViewer fireworks;\r\n\r\n    public Main() {\r\n        //We create the fireworks. Please note that different pathways overview cannot be loaded in the same instance\r\n        fireworks = FireowksFactory.createFireworksViewer();\r\n    }\r\n\r\n    @Override\r\n    public void onModuleLoad() {\r\n        Scheduler.get().scheduleDeferred(new Scheduler.ScheduledCommand() {\r\n            @Override\r\n            public void execute() {\r\n                FireworksFactory.CONSOLE_VERBOSE = true;   //This is optional (for dev purposes)\r\n                FireworksFactory.EVENT_BUS_VERBOSE = true; //This is optional (for dev purposes)\r\n\r\n                //For this use case we add the firewoks as the main and only object of the webpage\r\n                RootLayoutPanel.get().add(fireworks);\r\n            }\r\n        });\r\n    }\r\n}\r\n</code></pre>\r\n<h3><a href=\"#API\" id=\"API\"><i class=\"fa fa-code\" style=\"font-size: 23px;\"></i>API</a></h3>\r\n<p>The available methods are defined in the <b>DiagramViewer</b> interface in the <b>org.reactome.web.diagram.client</b> package. These are:</p>\r\n<pre><code>    //subscribe to this event to be notified when the user closes the analysis overlay\r\n    HandlerRegistration addAnalysisResetHandler(AnalysisResetHandler handler);\r\n\r\n    //Subscribe to this event to be notified if the browser does not support canvas, so diagrams can not be displayed\r\n    HandlerRegistration addCanvasNotSupportedHandler(CanvasNotSupportedHandler handler);\r\n\r\n    //Subscribe to this event to be notified when the overview is displayed in the viewport\r\n    HandlerRegistration addFireworksLoaded(FireworksLoadedHandler handler);\r\n\r\n    //Subscribe to this event to be notified when\r\n    HandlerRegistration addExpressionColumnChangedHandler(ExpressionColumnChangedHandler handler);\r\n\r\n    //Subscribe to this event to be notified when the user hovers over an node in the overview\r\n    HandlerRegistration addNodeHoverHandler(NodeHoverHandler handler);\r\n\r\n    //Subscribe to this event to be notified when the user leave the hovered node in the overview\r\n    HandlerRegistration addNodeHoverResetHandler(NodeHoverResetHandler handler);\r\n\r\n    //Subscribe to this event to be notified when the user opens a node in the overview\r\n    HandlerRegistration addNodeOpenedHandler(NodeOpenedHandler handler);\r\n\r\n    //Subscribe to this event to be notified when the user selects a node in the overview\r\n    HandlerRegistration addNodeSelectedHandler(NodeSelectedHandler handler);\r\n\r\n    //Subscribe to this event to be notified when the user unselects a node in the overview\r\n    HandlerRegistration addNodeSelectedResetHandler(NodeSelectedResetHandler handler);\r\n\r\n    //Subscribe to this event to be notified when the user changes the colour profile\r\n    HandlerRegistration addProfileChangedHandler(ProfileChangedHandler handler);\r\n\r\n    //Returns the selected node\r\n    Node getSelected();\r\n\r\n    //Highlights the node with the provided stable identifier\r\n    void highlightNode(String stableIdentifier);\r\n\r\n    //Highlights the node with the provided stable identifier\r\n    void highlightNode(Long dbIdentifier);\r\n\r\n    //Zooms-in to the node with the provided stable identifier\r\n    void openPathway(String stableIdentifier);\r\n\r\n    //Zooms-in to the node with the provided database identifier\r\n    void openPathway(Long dbIdentifier);\r\n\r\n    //Resets the analysis overlay\r\n    void resetAnalysis();\r\n\r\n    //Resets the highlighted nodes\r\n    void resetHighlight();\r\n\r\n    //Resets the selected nodes\r\n    void resetSelection();\r\n\r\n    //Selects the node with the provided stable identifier\r\n    void selectNode(String stableIdentifier);\r\n\r\n    //Selects the node with the provided database identifier\r\n    void selectNode(Long dbIdentifier);\r\n\r\n    //Sets the analysis overlay for a given \"token\" and \"resource\" (this will query the Analysis Service)\r\n    void setAnalysisToken(String token, String resource);\r\n\r\n    //Show all the nodes in the viewport\r\n    void showAll();</code></pre>\r\n<h3><a href=\"#Resources\" id=\"Resources\"><i class=\"fa fa-book\" style=\"font-size: 23px;\"></i>Resources</a></h3>\r\n<ul style=\"list-style: none;\">\r\n<li style=\"line-height: 30px;\"><a href=\"http://github.com/reactome/Fireworks\"><i class=\"fa fa-github\" style=\"padding-right: 5px;\"></i>GWT Fireworks code</a></li>\r\n<li style=\"line-height: 30px;\"><a href=\"http://github.com/reactome-pwp\"><i class=\"fa fa-github\" style=\"padding-right: 5px;\"></i>Reactome Pathways Portal code</a></li>\r\n</ul>\r\n</div>','',1,29,'2017-01-01 09:00:00',156,'','2017-01-01 09:00:00',156,0,'0000-00-00 00:00:00','2017-06-07 10:16:02','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}',1,5,'','',1,0,'{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}',0,'*',''),(58,633,'Diagram JS','diagram-js','<style type=\"text/css\">pre, pre code {\r\n    word-wrap: initial;\r\n    white-space: pre;\r\n    word-break: break-all;\r\n}\r\n}</style>\r\n<p>Reactome\'s DiagramJs widget is our diagram viewer in an ordinary JavaScript API. It is meant to be reused by third party resources in order to display Reactome Pathway Diagrams directly in their web pages and enable users to interact with them.</p>\r\n<div id=\"diagramHolder\" class=\"diagram\"></div>\r\n<p>&nbsp;</p>\r\n<h3><a href=\"#reuse-diagram-widget\" id=\"reuse-diagram-widget\"><i class=\"fa fa-rocket\" style=\"font-size: 23px;\"></i>Reusing Reactome\'s Diagram Widget?</a></h3>\r\n<p>To reuse our viewer you need to follow the following steps</p>\r\n<p>1. Include the diagram javascript dependency in you HTML header</p>\r\n<pre><code>&lt;script type=\"text/javascript\" language=\"javascript\" src=\"http://www.reactome.org/DiagramJs/diagram/diagram.nocache.js\"&gt;&lt;/script&gt;</code></pre>\r\n<p>2. Add a place holder in the body of your web page</p>\r\n<pre><code>&lt;div id=\"diagramHolder\"&gt;&lt;/div&gt;</code></pre>\r\n<p>3. Create and initialise the diagram viewer from your javascript code</p>\r\n<pre><code>    //Creating the Reactome Diagram widget\r\n    //Take into account a proxy needs to be set up in your server side pointing to www.reactome.org\r\n    function onReactomeDiagramReady(){  //This function is automatically called when the widget code is ready to be used\r\n        var diagram = Reactome.Diagram.create({\r\n            \"placeHolder\" : \"diagramHolder\",\r\n            \"width\" : 900,\r\n            \"height\" : 500\r\n        });\r\n\r\n        //Initialising it to the \"Hemostasis\" pathway\r\n        diagram.loadDiagram(\"R-HSA-109582\");\r\n\r\n        //Adding different listeners\r\n\r\n        diagram.onDiagramLoaded(function (loaded) {\r\n            console.info(\"Loaded \", loaded);\r\n            diagram.selectItem(\"R-HSA-109582\");\r\n            diagram.flagItems(\"FYN\");\r\n        });\r\n\r\n        diagram.onObjectHovered(function (hovered){\r\n            console.info(\"Hovered \", hovered);\r\n        });\r\n\r\n        diagram.onObjectSelected(function (selected){\r\n            console.info(\"Selected \", selected);\r\n        });\r\n    }</code></pre>\r\n<h3><a href=\"#API\" id=\"API\"><i class=\"fa fa-code\" style=\"font-size: 23px;\"></i>DiagramJs API</a></h3>\r\n<p>The current implementation supports the following listeners and methods:</p>\r\n<div id=\"r-responsive-table\">\r\n<table class=\"reactome\">\r\n<thead>\r\n<tr><th>Method</th><th>Params</th><th>Description</th></tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td data-label=\"Method\"><b>create</b> :: Constructor<br /> &nbsp;&nbsp;Reactome.Diagram.create(params);</td>\r\n<td data-label=\"Params\"><b>param</b> :: json object<br /> &nbsp;{<br /> &nbsp;&nbsp;&nbsp;\'proxyPrefix\' : string,<br /> &nbsp;&nbsp;&nbsp;\'placeHolder\' : string,<br /> &nbsp;&nbsp;&nbsp;\'width\' : int (optional),<br /> &nbsp;&nbsp;&nbsp;\'height\' : int (optional)<br /> &nbsp;}</td>\r\n<td data-label=\"Description\">Creates and returns a new Reactome.Diagram object</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>loadDiagram(stId)</b> :: void</td>\r\n<td data-label=\"Params\">Pathway stable identifier<br /> <b>stId</b> : string</td>\r\n<td data-label=\"Description\">Loads the specified pathway</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>flagItems(term)</b> :: void</td>\r\n<td data-label=\"Params\">Entity identifier (gene name)<br /> <b>term</b> : string</td>\r\n<td data-label=\"Description\">Flags all entities matching with the \"term\"</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>highlightItem(stId)</b> :: void</td>\r\n<td data-label=\"Params\">Item stable identifier<br /> <b>stId</b> : string</td>\r\n<td data-label=\"Description\">Highlights the specified item if it exists in the diagram</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>resetAnalysis()</b> :: void</td>\r\n<td data-label=\"Params\">&nbsp;</td>\r\n<td data-label=\"Description\">Resets the analysis overlay</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>resetFlaggedItems()</b> :: void</td>\r\n<td data-label=\"Params\">&nbsp;</td>\r\n<td data-label=\"Description\">Resets the flagged items</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>resetHighlight()</b> :: void</td>\r\n<td data-label=\"Params\">&nbsp;</td>\r\n<td data-label=\"Description\">Clears the highlights in the diagram</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>resetSelection()</b> :: void</td>\r\n<td data-label=\"Params\">&nbsp;</td>\r\n<td data-label=\"Description\">Clears the selection in the diagram</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>resize(width, height)</b> :: void</td>\r\n<td data-label=\"Params\"><b>widht</b>: int<br /> <b>height</b>: int</td>\r\n<td data-label=\"Description\">Resizes the viewport to the specified width and height</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>selectItem(stId)</b> :: void</td>\r\n<td data-label=\"Params\">Item stable identifier<br /> <b>stId</b> : string</td>\r\n<td data-label=\"Description\">Selects the specified item if it exists in the diagram</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>setAnalysisToken(token, resource)</b> :: void</td>\r\n<td data-label=\"Params\">Analysis token<br /> <b>token</b> : string<br /> Resource<br /> <b>resource</b> : string</td>\r\n<td data-label=\"Description\">Overlays the analysis result corresponding to the specified (token, resource)</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onObjectSelected(function(obj))</b> :: void</td>\r\n<td data-label=\"Params\"><b>obj</b> is selected item:<br /> &nbsp;{<br /> &nbsp;&nbsp;&nbsp;\'stId\' : string,<br /> &nbsp;&nbsp;&nbsp;\'displayName\' : string,<br /> &nbsp;&nbsp;&nbsp;\'schemaClass\' : string,<br /> &nbsp;&nbsp;&nbsp;\'identifier\' : string (optional),<br /> &nbsp;&nbsp;&nbsp;\'geneNames\' : Array&lt;string&gt; (optional)<br /> &nbsp;}</td>\r\n<td data-label=\"Description\">The function is called when an object in the diagram is selected by the user action</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onObjectHovered(function(obj))</b> :: void</td>\r\n<td data-label=\"Params\"><b>obj</b> is hovered item:<br /> &nbsp;{<br /> &nbsp;&nbsp;&nbsp;\'stId\' : string,<br /> &nbsp;&nbsp;&nbsp;\'displayName\' : string,<br /> &nbsp;&nbsp;&nbsp;\'schemaClass\' : string,<br /> &nbsp;&nbsp;&nbsp;\'identifier\' : string (optional),<br /> &nbsp;&nbsp;&nbsp;\'geneNames\' : Array&lt;string&gt; (optional)<br /> &nbsp;}</td>\r\n<td data-label=\"Description\">The function is called when an object in the diagram is hovered by the user action</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onDiagramLoaded(function(stId))</b> :: void</td>\r\n<td data-label=\"Params\">The stable identifier of the loaded diagram<br /> <b>stId</b>: string</td>\r\n<td data-label=\"Description\">The function is called when a diagram is loaded in the viewer</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onFlagsReset(function())</b> :: void</td>\r\n<td data-label=\"Params\">The function receives no parameters</td>\r\n<td data-label=\"Description\">The function is called when the flagged items have been reset by the user action</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onAnalysisReset(function())</b> :: void</td>\r\n<td data-label=\"Params\">The function receives no parameters</td>\r\n<td data-label=\"Description\">The function is called when the users resets the analysis overlay<br /><br /> diagram.onAnalysisReset(function(){ /* your code here */ });</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onCanvasNotSupported(function())</b> :: void</td>\r\n<td data-label=\"Params\">The function receives no parameters.</td>\r\n<td data-label=\"Description\">The function is called when the browser doesn\'t support HTML5 Canvas so the viewer cannot be instantiated<br /><br /> diagram.onCanvasNotSupported(function(){ /* your code here */ });</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<script type=\"text/javascript\">jQuery(\'base\').remove();\r\nfunction getMainContentWidth(){\r\n   var DIV = \"fav-maincontent\";\r\n   var style = window.getComputedStyle(document.getElementById(DIV), null);\r\n   var width = style.getPropertyValue(\"width\").replace(\"px\",\"\");\r\n   var paddingLeft = style.getPropertyValue(\"padding-left\").replace(\"px\",\"\"); \r\n   var paddingRight = style.getPropertyValue(\"padding-right\").replace(\"px\",\"\");\r\n   return width - paddingLeft - paddingRight;\r\n}\r\njQuery( window ).resize(function() {\r\n  diagram.resize(getMainContentWidth(), 600);  \r\n});\r\nfunction onReactomeDiagramReady() {\r\n        diagram = Reactome.Diagram.create({\r\n            \"placeHolder\": \"diagramHolder\",\r\n            \"width\": getMainContentWidth(),\r\n            \"height\": 600\r\n        });\r\n        diagram.loadDiagram(\"R-HSA-109582\");\r\n        diagram.onDiagramLoaded(function (loaded) {\r\n            diagram.selectItem(\"R-HSA-75892\");\r\n            diagram.flagItems(\"FYN\");\r\n        });\r\n    }</script>\r\n<script src=\"/DiagramJs/diagram/diagram.nocache.js\" type=\"text/javascript\" language=\"javascript\"></script>\r\n</div>','',1,29,'2017-01-01 09:00:00',156,'','2017-01-01 09:00:00',156,0,'0000-00-00 00:00:00','2017-06-07 15:17:14','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}',1,4,'','',1,0,'{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}',0,'*',''),(59,634,'Diagram GWT','diagram-gwt','<p>Reactome’s Diagram GWT widget is our original implementation of the diagram viewer in <a href=\"http://www.gwtproject.org\" target=\"_blank\" rel=\"nofollow noopener noreferrer\">GWT</a>. It is meant to be reused by third party resources in order to display Reactome Pathway Diagrams directly in their web pages and enable users to interact with them.</p>\r\n<p>{loadposition dev-guide-pd-gwt-1}</p>\r\n<div>\r\n<h3><a href=\"#MoreInfo\" id=\"MoreInfo\"><i class=\"fa fa-question-circle\" style=\"font-size: 23px;\"></i>More Info</a></h3>\r\n<p><a href=\"http://www.gwtproject.org/\">GWT</a>&nbsp;is a development toolkit for building and optimizing complex browser-based applications. Its goal is to enable productive development of high-performance web applications without the developer having to be an expert in browser quirks, XMLHttpRequest, and JavaScript. GWT is used by many products at Google, including AdWords, AdSense, Flights, Hotel Finder, Offers, Wallet, Blogger. It’s open source, completely free, and used by thousands of developers around the world.</p>\r\n<p>&nbsp;</p>\r\n<h3><a href=\"#GetStarted\" id=\"GetStarted\"><i class=\"fa fa-rocket\" style=\"font-size: 23px;\"></i>Get Started</a></h3>\r\n<p>We assume that you already have your <a href=\"http://www.gwtproject.org/gettingstarted.html\">GWT project</a> up and running configured with the <a href=\"http://www.gwtproject.org/doc/latest/RefCommandLineTools.html\"> MAVEN archetype</a>. So the first step is to add the EBI Nexus repository in your pom.xml file:</p>\r\n<pre><code>&lt;repositories&gt;\r\n    ...\r\n    &lt;!-- EBI repo --&gt;\r\n    &lt;repository&gt;\r\n        &lt;id&gt;nexus-ebi-repo&lt;/id&gt;\r\n        &lt;name&gt;The EBI internal repository&lt;/name&gt;\r\n        &lt;url&gt;http://www.ebi.ac.uk/Tools/maven/repos/content/groups/ebi-repo/&lt;/url&gt;\r\n        &lt;releases&gt;\r\n            &lt;enabled&gt;true&lt;/enabled&gt;\r\n        &lt;/releases&gt;\r\n        &lt;snapshots&gt;\r\n            &lt;enabled&gt;false&lt;/enabled&gt;\r\n        &lt;/snapshots&gt;\r\n    &lt;/repository&gt;\r\n    &lt;!-- EBI SNAPSHOT repo --&gt;\r\n    &lt;repository&gt;\r\n        &lt;id&gt;nexus-ebi-snapshot-repo&lt;/id&gt;\r\n        &lt;name&gt;The EBI internal snapshot repository&lt;/name&gt;\r\n        &lt;url&gt;http://www.ebi.ac.uk/Tools/maven/repos/content/groups/ebi-snapshots/&lt;/url&gt;\r\n        &lt;releases&gt;\r\n            &lt;enabled&gt;false&lt;/enabled&gt;\r\n        &lt;/releases&gt;\r\n        &lt;snapshots&gt;\r\n            &lt;enabled&gt;true&lt;/enabled&gt;\r\n        &lt;/snapshots&gt;\r\n    &lt;/repository&gt;\r\n&lt;/repositories&gt;\r\n</code></pre>\r\n<p>Once the repository is in place, the next thing to do is adding the Reactome\'s pathways overview dependency:</p>\r\n<pre><code>&lt;dependencies&gt;\r\n    ...\r\n    &lt;dependency&gt;\r\n        &lt;groupId&gt;org.reactome.web&lt;/groupId&gt;\r\n        &lt;artifactId&gt;diagram&lt;/artifactId&gt;\r\n        &lt;version&gt;3.2.1&lt;/version&gt;\r\n    &lt;/dependency&gt;\r\n&lt;dependencies&gt;\r\n</code></pre>\r\n<p>Before writing the code to use the pathways overview, add the dependency in your <b>gwt.xml</b> file, so the compiler will find the pathways overview:</p>\r\n<pre><code>&lt;inherits name=\"org.reactome.web.diagram.DiagramViewer\" /&gt;\r\n</code></pre>\r\n<p>And finally, assuming the widget is now going to be displayed by its own, the code in your <b>EntryPoint</b> class would look like:</p>\r\n<pre><code>/**\r\n * For test purposes we assume the diagram loads as a single item in the web page.\r\n *\r\n * Entry point classes define onModuleLoad().\r\n */\r\npublic class Main implements EntryPoint {\r\n\r\n    private final DiagramViewer diagram;\r\n\r\n    public Main() {\r\n        //We create the diagram. Please note that different pathways diagrams can be loaded in the same\r\n        //instance (the diagram manages the cache of previously loaded diagrams using an LRU)\r\n        diagram = DiagramFactory.createDiagramViewer();\r\n    }\r\n\r\n    @Override\r\n    public void onModuleLoad() {\r\n        Scheduler.get().scheduleDeferred(new Scheduler.ScheduledCommand() {\r\n            @Override\r\n            public void execute() {\r\n                DiagramFactory.CONSOLE_VERBOSE = true;   //This is optional (for dev purposes)\r\n                DiagramFactory.EVENT_BUS_VERBOSE = true; //This is optional (for dev purposes)\r\n\r\n                //For this use case we add the diagram as the main and only object of the webpage\r\n                RootLayoutPanel.get().add(diagram);\r\n                \r\n                //This will run the loadDiagram method asynchronously, ensuring the DOM has been updated\r\n                Scheduler.get().scheduleDeferred(new Scheduler.ScheduledCommand() {\r\n                    @Override\r\n                    public void execute() {\r\n                        diagram.loadDiagram(\"R-HSA-111804\");\r\n                    }\r\n                });\r\n            }\r\n        });\r\n    }\r\n}</code></pre>\r\n<h3><a href=\"#API\" id=\"API\"><i class=\"fa fa-code\" style=\"font-size: 23px;\"></i>API</a></h3>\r\n<p>The available methods are defined in the <b>DiagramViewer</b> interface in the <b>org.reactome.web.diagram.client</b> package. These are:</p>\r\n<pre><code>    //subscribe to this event to be notified when the user closes the analysis overlay\r\n    HandlerRegistration addAnalysisResetHandler(AnalysisResetHandler handler);\r\n\r\n    //Subscribe to this event to be notified if the browser does not support canvas, so diagrams can not be displayed\r\n    HandlerRegistration addCanvasNotSupportedEventHandler(CanvasNotSupportedHandler handler);\r\n\r\n    //Subscribe to this event to be notified when the user selects an object in the diagram\r\n    HandlerRegistration addDatabaseObjectSelectedHandler(GraphObjectSelectedHandler handler);\r\n\r\n    //Subscribe to this event to be notified when the user hovers over an object in the diagram\r\n    HandlerRegistration addDatabaseObjectHoveredHandler(GraphObjectHoveredHandler handler);\r\n\r\n    //Subscribe to this event to be notified when a new diagram is displayed in the viewport\r\n    HandlerRegistration addDiagramLoadedHandler(DiagramLoadedHandler handler);\r\n\r\n    //Subscribe to this event to be notified when elements in the diagram are flagged\r\n    HandlerRegistration addDiagramObjectsFlaggedHandler(DiagramObjectsFlaggedHandler handler);\r\n\r\n    //Subscribe to this event to be notified when flagged elements in the diagram are not longer visible\r\n    HandlerRegistration addDiagramObjectsFlagResetHandler(DiagramObjectsFlagResetHandler handler);\r\n\r\n    //Subscribe to this event to be notified when the user selects the \"Go to overview\" button\r\n    HandlerRegistration addFireworksOpenedHandler(FireworksOpenedHandler handler);\r\n\r\n    //Flags the elements in the diagram which identifiers match with \"identifier\"\r\n    void flagItems(String identifier);\r\n\r\n    //Highlights the element(s) in the diagram with the \"stableIdentifier\"\r\n    void highlightItem(String stableIdentifier);\r\n\r\n    //Highlights the element(s) in the diagram with the \"dbIdentifier\"\r\n    void highlightItem(Long dbIdentifier);\r\n\r\n    //Loads the diagram with the \"stableIdentifier\"\r\n    void loadDiagram(String stId);\r\n\r\n    //Loads the diagram with the \"dbId\"\r\n    void loadDiagram(Long dbId);\r\n\r\n    //Resets the analysis overlay\r\n    void resetAnalysis();\r\n\r\n    //Resets the flagged items\r\n    void resetFlaggedItems();\r\n\r\n    //Resets the highlighted items\r\n    void resetHighlight();\r\n\r\n    //Resets the selected items\r\n    void resetSelection();\r\n\r\n    //Selects the element(s) in the diagram with the \"stableIdentifier\"\r\n    void selectItem(String stableIdentifier);\r\n\r\n    //Selects the element(s) in the diagram with the \"stableIdentifier\"\r\n    void selectItem(Long dbIdentifier);\r\n\r\n    //Sets the analysis overlay for a given \"token\" and \"resource\" (this will query the Analysis Service)\r\n    void setAnalysisToken(String token, String resource);\r\n\r\n    //Set the widget visibility\r\n    void setVisible(boolean visible);</code></pre>\r\n<h3><a href=\"#Resources\" id=\"Resources\"><i class=\"fa fa-book\" style=\"font-size: 23px;\"></i>Resources</a></h3>\r\n<ul style=\"list-style: none;\">\r\n<li style=\"line-height: 30px;\"><a href=\"http://github.com/reactome-pwp/diagram\"><i class=\"fa fa-github\" style=\"padding-right: 5px;\"></i>GWT Diagram code</a></li>\r\n<li style=\"line-height: 30px;\"><a href=\"http://github.com/reactome-pwp\"><i class=\"fa fa-github\" style=\"padding-right: 5px;\"></i>Reactome Pathways Portal code</a></li>\r\n</ul>\r\n</div>','',1,29,'2017-01-01 09:00:00',156,'','2017-01-01 09:00:00',156,0,'0000-00-00 00:00:00','2017-06-07 15:17:38','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}',1,3,'','',1,0,'{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}',0,'*',''),(60,638,'Identifiers Mapping','identifiers-mapping','<p>The mapping files consist of a tab-separated table that indicates which external protein, gene or small molecule identifiers in the source database were mapped to Reactome pathway and reaction annotations. Our goal with distributing three sets of files for each different identifier type is to provide these mapping files link the source database identifier to:</p>\r\n<ul>\r\n<li style=\"list-style-type: lower-roman;\">the lowest level pathway diagram or subset of the pathway</li>\r\n<li style=\"list-style-type: lower-roman;\">all level pathway diagrams (filename appended with \'All_Levels’),</li>\r\n<li style=\"list-style-type: lower-roman;\">all reaction events.</li>\r\n</ul>\r\n<p>The columns within the mappings files follow a similar format:</p>\r\n<ol>\r\n<li>Source database identifier, e.g. UniProt, ENSEMBL, NCBI Gene or ChEBI identifier</li>\r\n<li>&nbsp;Reactome Pathway Stable identifier</li>\r\n<li>URL</li>\r\n<li>Event (Pathway or Reaction) Name</li>\r\n<li>Evidence Code</li>\r\n<li>Species</li>\r\n</ol>\r\n<p>If you have any further questions, please <a href=\"mailto:help@reactome.org?subject=Identifier%20Mapping%20File\">contact us</a>.</p>','',1,33,'2017-01-01 09:00:00',156,'','2017-01-01 09:00:00',156,0,'0000-00-00 00:00:00','2017-06-23 09:19:03','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"article_layout\":\"_:default\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}',1,4,'','',1,0,'{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}',0,'*',''),(61,639,'Pathway Information','pathway-information','<p>The pathway information files consist of tab-separated tables that provide information about Reactome pathway hierarchy:</p>\r\n<ol>\r\n<li>The \"Complete list of pathways\" file maps the Reactome Stable identifier (ST_ID) to a pathway name and corresponding species.</li>\r\n<li>The \"Pathway hierarchy relationship\" file consists of two columns of Reactome Stable identifiers (ST_ID), defining the relationship between pathways within the pathway hierarchy. The first column provides the parent pathway stable identifier, whereas the second column provides the child pathway stable identifier.</li>\r\n</ol>','',1,2,'2017-01-01 09:00:00',156,'','2017-01-01 09:00:00',156,0,'0000-00-00 00:00:00','2017-06-23 09:37:27','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}',1,4,'','',1,0,'{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}',0,'*',''),(62,640,'Tab-Delimiter Format','tab-delimiter-format','<p>Interactions are generated electronically based on the data that we store on complexes and reactions, i.e. the interactions provided by Reactome are not curated and are not experimental data. In addition, the complexes and reactions in species other than human are derived by orthology inference from the correspondinghuman complexes and reactions.</p>\r\n<p>The meaning of \"interaction\" is quite broad: 2 protein sequences occur in the same complex or they occur in the same reaction.</p>\r\n<p>The 1st line begins with \'#\' and gives the number of unique interactions, i.e. unique pairs if UniProt accession numbers.</p>\r\n<p>The rest of the file is tab delimited with columns having following meaning/content:</p>\r\n<ul style=\"list-style: decimal;\">\r\n<li>Protein ID (e.g. UniProt accession) for interactor 1.</li>\r\n<li>Ensembl gene id(s delimited by \'|\') for interactor 1. Note that one UniProt entry may be mapped to multiple Ensembl genes.</li>\r\n<li>Entrez Gene id(s delimited by \'|\') for interactor 1. Note that one UniProt entry may be mapped to multiple Entrez Gene ids.</li>\r\n<li>Protein ID (e.g. UniProt accession) for interactor 2.</li>\r\n<li>Ensembl gene id(s delimited by \'|\') for interactor 2. Note that one UniProt entry may be mapped to multiple Ensembl genes.</li>\r\n<li>Entrez Gene id(s delimited by \'|\') for interactor 2. Note that one UniProt entry may be mapped to multiple Entrez Gene ids.</li>\r\n<li>Type of interaction, either \'direct_complex\', \'indirect_complex\', \'reaction\' or \'neighbouring_reaction\'. Meanings of those below.</li>\r\n<li>Interaction context. This is the class and internal identifier of Reactome instance via which this interaction happens. For \'neighbouring_reaction\' type interaction this contains a pair of class:identifier string separated by \'\'.</li>\r\n<li>Comma-delimited list of PubMed identifiers supporting this interaction. For a reaction these are the PMIDs from the Reaction.literatureReference slot (and not from the \"second tier\" Reaction.summation.literatureReference). For a complex these are from reations which produce the complex. For \'neighbouring_reaction\' type interaction this field is empty.</li>\r\n</ul>\r\n<h3>Interaction types</h3>\r\n<p>In Reactome, complexes can be arbitrarily nested, i.e., contain subcomplexes. Here we have complex C1 consisting of complexes C2 and C3. C2 consist of molecules A and B. C3 consists of molecules C and D:</p>\r\n<pre>           C1 --- C2 --- A\r\n                |      |\r\n                |      |- B\r\n                |\r\n                |- C3 --- C\r\n                |\r\n                |- D\r\n                </pre>\r\n<p>Now really to the interaction types:</p>\r\n<ul>\r\n<li>direct_complex - interactors are directly in the same complex, i.e. w/o further nested complexes. From the example interactions A B and C are of this type.</li>\r\n<li>indirect_complex - interactors in different subcomplexes of a complex. From the example above interactions AC, AD, BC and BD are of this type.</li>\r\n<li>reaction - interactors participate in the same reaction. Only those reactions are reported for which the intreactors are not complexed (with the exception being association dissociation reactions which are reported).</li>\r\n<li>neighbouring_reaction - interactor participate in 2 \"consecutive\" reactions, i.e. when one reaction produces a PhysicalEntity which is either an input or a catalyst for another reaction. However, to avoid the \"trivial\" (i.e. over ATP, ADP etc) interactions, the computation is done using the \'precedingEvent\' attribute used to order reactions in a pathway.</li>\r\n</ul>\r\n<p>The \'direct_complex\' type doesn\'t really mean true pairwise interaction. For example, Reactome\'s representation of ribosomal S60 subunit consists \"directly\" of 40+ proteins and has no sub-complexes. Obviously this can hardly mean that each one of those component proteins directly interacts with every other protein. Hence, the \'direct_complex\' vs. \'indirect_complex\' distinction is quite meaningless.</p>\r\n<h3>Other notes:</h3>\r\n<ul>\r\n<li>If the same interaction (i.e same UniProte accession pair) happens in multiple contexts, each context is reported on a separate row and hence the row number in the file is bigger than the number of unique interactions reported on the 1st line.</li>\r\n<li>The order of interactors on one line has no meaning (they are just ordered according to the Reactome internal identifier...). Every interaction with context is reported once, i.e. AB is not reported as BA.</li>\r\n</ul>','',1,33,'2017-01-01 09:00:00',156,'','2017-01-01 09:00:00',156,0,'0000-00-00 00:00:00','2017-06-23 10:02:36','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"article_layout\":\"_:default\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}',1,4,'','',1,0,'{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}',0,'*',''),(63,641,'Reactome Curator Tool 3','reactome-curator-tool-3','<style type=\"text/css\">ul{ list-style-type: square; }</style>\r\n<div id=\"top\"></div>\r\n<center>\r\n<p><span><b>Available Installers</b></span></p>\r\n<table border=\"0\" cellspacing=\"10\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td>&nbsp;</td>\r\n<td><span>Platform</span></td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td><center><span>Installer</span></center></td>\r\n<td>&nbsp;</td>\r\n<td><center><span>Instructions</span></center></td>\r\n</tr>\r\n<tr>\r\n<td style=\"text-align: center;\" valign=\"center\"><i class=\"fa fa-windows\"></i></td>\r\n<td valign=\"bottom\">\r\n<div style=\"text-align: left;\"><span>Windows</span></div>\r\n</td>\r\n<td valign=\"bottom\">&nbsp;</td>\r\n<td valign=\"bottom\">&nbsp;</td>\r\n<td valign=\"bottom\"><center><a href=\"/download/tools/curatorTool/InstData/win_linux/install.zip\">Download (15.4M)</a></center></td>\r\n<td valign=\"bottom\">&nbsp;</td>\r\n<td valign=\"bottom\"><center><a href=\"#windows\">View</a></center></td>\r\n</tr>\r\n<tr>\r\n<td style=\"text-align: center;\" valign=\"center\"><i class=\"fa fa-apple\"></i></td>\r\n<td valign=\"bottom\">\r\n<div style=\"text-align: left;\"><span>Mac OS X</span></div>\r\n</td>\r\n<td valign=\"bottom\">&nbsp;</td>\r\n<td valign=\"bottom\">&nbsp;</td>\r\n<td valign=\"bottom\"><center><a href=\"/download/tools/curatorTool/InstData/mac/install.zip\">Download (79M)</a></center></td>\r\n<td valign=\"bottom\">&nbsp;</td>\r\n<td valign=\"bottom\"><center><a href=\"#macosx\">View</a></center></td>\r\n</tr>\r\n<tr>\r\n<td style=\"text-align: center;\" valign=\"center\"><i class=\"fa fa-linux\"></i></td>\r\n<td valign=\"bottom\">\r\n<div style=\"text-align: left;\"><span>Linux</span></div>\r\n</td>\r\n<td valign=\"bottom\">&nbsp;</td>\r\n<td valign=\"bottom\">&nbsp;</td>\r\n<td valign=\"bottom\"><center><a href=\"/download/tools/curatorTool/InstData/win_linux/install.zip\">Download (15.4M)</a></center></td>\r\n<td valign=\"bottom\">&nbsp;</td>\r\n<td valign=\"bottom\"><center><a href=\"#linux\">View</a></center></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</center><hr />\r\n<div id=\"windows\" style=\"text-align: left;\">\r\n<h4><a href=\"#windows\" name=\"windows\">Windows:</a></h4>\r\n<h5>Instructions</h5>\r\n<ul>\r\n<li>After downloading, double-click&nbsp;<b><code>install.zip</code></b>&nbsp;to unzip it to an empty folder. This folder should be named \"ReactomeCuratorTool\" though any name can be used. Please use the latest version of WinZip if you use this application for unzipping.</li>\r\n<li>After unzipping, an application called ReactomeCuratorTool.exe should be found in the named folder. Double click this application to launch the Reactome Curator Tool.</li>\r\n</ul>\r\n<h5>Notes</h5>\r\n<ul>\r\n<li>You need to install a Java 1.5.0 (or later) virtual machine. You can download one from&nbsp;<a href=\"http://java.sun.com/\" target=\"_blank\" rel=\"noopener noreferrer\">Sun\'s Java web site</a>.</li>\r\n</ul>\r\n<p>(<a href=\"#top\">Go To Top</a>)</p>\r\n</div>\r\n<center><hr /></center>\r\n<div id=\"macosx\" style=\"text-align: left;\">\r\n<h4><a href=\"#macosx\" name=\"macosx\">Mac OS X:</a></h4>\r\n<h5>Instructions</h5>\r\n<ul>\r\n<li>After downloading, double-click&nbsp;<b><code>install.zip</code></b>&nbsp;if it is not automatically unzipped.</li>\r\n<li>Double click ReactomeCuratorTool to launch the Reactome Curator Tool.</li>\r\n</ul>\r\n<h5>Notes</h5>\r\n<ul>\r\n<li>Requires Mac OS X 10.4.8 or later, and Java 1.5.0 or later.</li>\r\n<li>The compressed installer should be recognized by Stuffit Expander and should automatically be expanded after downloading. If it is not expanded, you can expand it manually using&nbsp;<a href=\"http://www.aladdinsys.com/expander/index.html\" target=\"_blank\" rel=\"noopener noreferrer\">StuffIt Expander 6.0 or later</a>.</li>\r\n<li>If you have any problems launching the installer once it has been expanded, make sure that the compressed installer was expanded using Stuffit Expander. If you continue to have problems, please contact technical support.</li>\r\n</ul>\r\n<p>(<a href=\"#top\">Go To Top</a>)</p>\r\n</div>\r\n<center><hr /></center>\r\n<div id=\"linux\" style=\"text-align: left;\">\r\n<h4><a href=\"#linux\" name=\"linux\">Linux:</a></h4>\r\n<h5>Instructions</h5>\r\n<ul>\r\n<li>After downloading open a shell, and&nbsp;<span><b><code>cd</code></b></span><span>&nbsp;to the directory where you downloaded the installer.</span></li>\r\n<li>At the prompt, type&nbsp;<span><b><code>unzip install.zip</code></b>&nbsp;to unzip the installer.</span></li>\r\n<li>Use ./ReactomeCuratorTool.sh to launch the Reactome Curator Tool.</li>\r\n</ul>\r\n<h5>Notes</h5>\r\n<ul>\r\n<li><span>You need to install a Java 1.5.0 (or later) virtual machine. You can download one from&nbsp;<a href=\"http://java.sun.com/\" target=\"_blank\" rel=\"noopener noreferrer\">Sun\'s Java web site</a>&nbsp;or contact your OS manufacturer.</span></li>\r\n</ul>\r\n<p>(<a href=\"#top\">Go To Top</a>)</p>\r\n</div>','',1,14,'2017-01-01 09:00:00',156,'','2017-01-01 09:00:00',156,0,'0000-00-00 00:00:00','2017-06-23 10:53:19','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\