uages[i];\r\n            var $content = jQuery($languageBlocks[language]);\r\n            var id;\r\n            if ($content.attr(\'id\')) {\r\n                id = $content.attr(\'id\');\r\n            } else {\r\n                id = idBase + \'-\' + language;\r\n                $content.attr(\'id\', id);\r\n            }\r\n            $content.addClass(\'tab-pane\').css(\'position\', \'relative\');\r\n            var $li = $LI.clone();\r\n            var $a = $A.clone();\r\n\r\n            $a.attr(\'href\', \'#\' + id).text(LANGUAGES[language]).data(\'lang\', language).on(\'shown.bs.tab\', selectTab).on(\'click\', focusSelectedExample);\r\n\r\n            if (language in languageEventElements) {\r\n                languageEventElements[language].push($a);\r\n            } else {\r\n                languageEventElements[language] = [$a];\r\n            }\r\n            $wrapper.append($content);\r\n\r\n            if (storedLanguage) {\r\n                if (language === storedLanguage) {\r\n                    $li.addClass(\'active\');\r\n                    $content.addClass(\'active\');\r\n                }\r\n            } else if (i === 0) {\r\n                $li.addClass(\'active\');\r\n                $content.addClass(\'active\');\r\n            }\r\n\r\n            $li.append($a);\r\n            $ul.append($li);\r\n        }\r\n        $exampleBlock.children(\'div.example-title\', this).first().after($ul);\r\n        $exampleBlock.append($wrapper);\r\n    }\r\n}\r\n\r\nfunction storageAvailable(type) {\r\n    try {\r\n        var storage = window[type];\r\n        var x = \'__storage_test__\';\r\n        storage.setItem(x, x);\r\n        storage.removeItem(x);\r\n        return true;\r\n    }\r\n    catch(e) {\r\n        return false;\r\n    }\r\n}\r\n\r\nfunction getCodeExampleLanguage() {\r\n    return storageAvailable(\'sessionStorage\') ? sessionStorage.getItem(\'code_example_language\') || false : false;\r\n}</script>\r\n<script type=\"text/javascript\">jQuery(document).ready(function($){\r\nCodeMirror.colorize();\r\ntabTheSource(jQuery(\'#rcypher\'));\r\n});</script>','',1,29,'2017-01-01 09:00:00',156,'','2017-01-01 09:00:00',156,0,'1000-01-01 00:00:00','2017-06-02 15:08:40','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"article_layout\":\"_:default\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}',1,7,'','',1,0,'{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}',0,'*','',''),(55,630,'Icons Info ','icons-info','<p>{loadposition icon-lib-pos-1}</p>','',1,14,'2017-01-01 09:00:00',156,'','2017-01-01 09:00:00',156,0,'1000-01-01 00:00:00','2017-06-05 15:23:33','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"article_layout\":\"\",\"show_title\":\"0\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}',1,6,'','',1,0,'{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}',0,'*','','Page with 2 options - EHLDs and Icon Library'),(56,631,'Pathways Overview JS','pathways-overview-js','<style type=\"text/css\">pre {\r\n    word-wrap: initial;\r\n    white-space: pre;\r\n    word-break: break-all;\r\n}\r\n}</style>\r\n<div>Reactome\'s Fireworks JS widget is our pathways overview viewer in an ordinary JavaScript API. It is meant to be reused by third party resources in order to display Reactome pathways overview directly in their web pages and enable users to interact with them.</div>\r\n<p>&nbsp;</p>\r\n<div id=\"fireworksHolder\" class=\"diagram\"></div>\r\n<div>\r\n<h3><a href=\"#reuse-pathways-widget\" id=\"reuse-pathways-widget\"><i class=\"fa fa-rocket\" style=\"font-size: 23px;\"></i>Reusing Reactome\'s Pathways Overview Widget?</a></h3>\r\n<p>To reuse our viewer you need to follow the following steps</p>\r\n<p>1. Include the fireworks javascript dependency in you HTML header</p>\r\n<pre class=\"code\">    &lt;script type=\"text/javascript\" language=\"javascript\" src=\"https://reactome.org/FireworksJs/fireworks/fireworks.nocache.js\"&gt;&lt;/script&gt;\r\n        </pre>\r\n<p>2. Add a place holder in the body of your web page</p>\r\n<pre class=\"prettyprint code\">    &lt;div id=\"fireworksHolder\"&gt;&lt;/div&gt;\r\n        </pre>\r\n<p>3. Set a proxy in your server under \"/reactome\" pointing to \"https://reactome.org\"</p>\r\n<p>4. Create and initialise the pathways overview from your javascript code</p>\r\n<pre class=\"prettyprint code\">    //Creating the Reactome pathways overview widget\r\n    function onReactomeFireworksReady(){  //This function is automatically called when the widget code is ready to be used\r\n        var fireworks = Reactome.Fireworks.create({\r\n            \"placeHolder\" : \"fireworksHolder\",\r\n            \"width\" : 930,\r\n            \"height\" : 500\r\n        });\r\n\r\n        //Adding different listeners\r\n\r\n        fireworks.onFireworksLoaded(function (loaded) {\r\n            console.info(\"Loaded \", loaded);\r\n        });\r\n\r\n        fireworks.onNodeHovered(function (hovered){\r\n            console.info(\"Hovered \", hovered);\r\n        });\r\n\r\n        fireworks.onNodeSelected(function (selected){\r\n            console.info(\"Selected \", selected);\r\n        });\r\n    }\r\n        </pre>\r\n<a>&nbsp;</a>\r\n<h3><a href=\"#API\" id=\"API\"><i class=\"fa fa-code\" style=\"font-size: 23px;\"></i>FireworksJs API</a></h3>\r\n<p>The current implementation supports the following listeners and methods:</p>\r\n<div id=\"r-responsive-table\">\r\n<table class=\"reactome\">\r\n<thead>\r\n<tr><th>Method</th><th>Params</th><th>Description</th></tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td data-label=\"Method\"><b>create</b> :: Constructor<br /> &nbsp;&nbsp;Reactome.Fireworks.create(params);</td>\r\n<td data-label=\"Params\"><b>param</b> :: json object<br /> &nbsp;{<br /> &nbsp;&nbsp;&nbsp;\'proxyPrefix\' : string,<br /> &nbsp;&nbsp;&nbsp;\'placeHolder\' : string,<br /> &nbsp;&nbsp;&nbsp;\'width\' : int (optional),<br /> &nbsp;&nbsp;&nbsp;\'height\' : int (optional)<br /> &nbsp;}</td>\r\n<td data-label=\"Description\">Creates and returns a new Reactome.Fireworks object</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>flagItems(String identifier)</b> :: void</td>\r\n<td data-label=\"Params\">Item identifier <br /> <b>identifier</b> : string</td>\r\n<td data-label=\"Description\">Flags pathways where the identifier is found. It accepts main identifiers but also cross references, gene names and physical entity stable identifiers</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>highlightNode(stId)</b> :: void</td>\r\n<td data-label=\"Params\">Item stable identifier<br /> <b>stId</b> : string</td>\r\n<td data-label=\"Description\">Highlights the specified item if it exists in the fireworks</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>resetAnalysis()</b> :: void</td>\r\n<td data-label=\"Params\">&nbsp;</td>\r\n<td data-label=\"Description\">Resets the analysis overlay</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>resetHighlight()</b> :: void</td>\r\n<td data-label=\"Params\">&nbsp;</td>\r\n<td data-label=\"Description\">Clears the highlights in the fireworks</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>resetSelection()</b> :: void</td>\r\n<td data-label=\"Params\">&nbsp;</td>\r\n<td data-label=\"Description\">Clears the selection in the fireworks</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>resize(width, height)</b> :: void</td>\r\n<td data-label=\"Params\"><b>width</b>: int<br /> <b>height</b>: int</td>\r\n<td data-label=\"Description\">Resizes the viewport to the specified with and height</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>selectNode(stId)</b> :: void</td>\r\n<td data-label=\"Params\">Item stable identifier<br /> <b>stId</b> : string</td>\r\n<td data-label=\"Description\">Selects the specified item if it exists in the fireworks</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>setAnalysisToken(token, resource)</b> :: void</td>\r\n<td data-label=\"Params\">Analysis token<br /> <b>token</b> : string<br /> Resource<br /> <b>resource</b> : string</td>\r\n<td data-label=\"Description\">Overlays the analysis result corresponding to the specified (token, resource)</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>showAll()</b> :: void</td>\r\n<td data-label=\"Params\">&nbsp;</td>\r\n<td data-label=\"Description\">Shows all the pathways in the viewport</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onNodesFlaggedReset(function())</b> :: void</td>\r\n<td data-label=\"Params\">The function receives no parameters</td>\r\n<td data-label=\"Description\">The function is called when the user resets the flagged items</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onNodeSelected(function(obj))</b> :: void</td>\r\n<td data-label=\"Params\"><b>obj</b> is selected item:<br /> &nbsp;{<br /> &nbsp;&nbsp;&nbsp;\'stId\' : string,<br /> &nbsp;&nbsp;&nbsp;\'displayName\' : string,<br /> &nbsp;&nbsp;&nbsp;\'schemaClass\' : string,<br /> &nbsp;}</td>\r\n<td data-label=\"Description\">The function is called when an object in the fireworks is selected by the user action</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onNodeHovered(function(obj))</b> :: void</td>\r\n<td data-label=\"Params\"><b>obj</b> is hovered item:<br /> &nbsp;{<br /> &nbsp;&nbsp;&nbsp;\'stId\' : string,<br /> &nbsp;&nbsp;&nbsp;\'displayName\' : string,<br /> &nbsp;&nbsp;&nbsp;\'schemaClass\' : string,<br /> &nbsp;}</td>\r\n<td data-label=\"Description\">The function is called when an object in the fireworks is hovered by the user action</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onFireworksLoaded(function(id))</b> :: void</td>\r\n<td data-label=\"Params\">The db identifier of the loaded species in fireworks<br /> <b>id</b>: string</td>\r\n<td data-label=\"Description\">The function is called when a fireworks is loaded in the viewer</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onAnalysisReset(function())</b> :: void</td>\r\n<td data-label=\"Params\">The function receives no parameters</td>\r\n<td data-label=\"Description\">The function is called when the users resets the analysis overlay<br /><br /> fireworks.onAnalysisReset(function(){ /* your code here */ });</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onCanvasNotSupported(function())</b> :: void</td>\r\n<td data-label=\"Params\">The function receives no parameters.</td>\r\n<td data-label=\"Description\">The function is called when the browser doesn\'t support HTML5 Canvas so the viewer cannot be instantiated<br /><br /> fireworks.onCanvasNotSupported(function(){ /* your code here */ });</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<script type=\"text/javascript\">function getMainContentWidth(){\r\n   var DIV = \"fav-maincontent\";\r\n   var style = window.getComputedStyle(document.getElementById(DIV), null);\r\n   var width = style.getPropertyValue(\"width\").replace(\"px\",\"\");\r\n   var paddingLeft = style.getPropertyValue(\"padding-left\").replace(\"px\",\"\"); \r\n   var paddingRight = style.getPropertyValue(\"padding-right\").replace(\"px\",\"\");\r\n   return width - paddingLeft - paddingRight;\r\n}\r\n\r\njQuery( window ).resize(function() {\r\n  fireworks.resize(getMainContentWidth(), 600);  \r\n});\r\n\r\nfunction onReactomeFireworksReady() {\r\n            fireworks = Reactome.Fireworks.create({\r\n                \"placeHolder\": \"fireworksHolder\",\r\n                \"width\": getMainContentWidth(),\r\n                \"height\": 600\r\n            });\r\n            fireworks.onFireworksLoaded(function (loaded) {\r\n                console.info(\"Loaded \", loaded);\r\n            });\r\n            fireworks.onNodeHovered(function (hovered) {\r\n                console.info(\"Hovered \", hovered);\r\n            });\r\n            fireworks.onNodeSelected(function (selected) {\r\n                console.info(\"Selected \", selected);\r\n            });\r\n            //fireworks.setAnalysisToken(\"MjAyMDEwMjAxMTU4NDFfMjkxMDA%253D\",\"TOTAL\");\r\n        }</script>\r\n<script src=\"/FireworksJs/fireworks/fireworks.nocache.js\" type=\"text/javascript\" language=\"javascript\"></script>\r\n</div>','',1,29,'2017-01-01 09:00:00',156,'','2017-01-01 09:00:00',156,0,'1000-01-01 00:00:00','2017-06-07 10:10:59','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}',1,6,'','',1,0,'{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}',0,'*','',''),(57,632,'Pathways Overview GWT','pathways-overview-gwt','<p>Reactome’s Fireworks GWT widget is our original implementation of the pathways overview in GWT. It is meant to be reused by third party resources in order to display Reactome pathways overview directly in their web pages and enable users to interact with them.</p>\r\n<p>{loadposition dev-guide-po-gwt-1}</p>\r\n<div>\r\n<h3><a href=\"#MoreInfo\" id=\"MoreInfo\"><i class=\"fa fa-question-circle\" style=\"font-size: 23px;\"></i>More Info</a></h3>\r\n<p><a href=\"http://www.gwtproject.org/\">GWT</a> is a development toolkit for building and optimizing complex browser-based applications. Its goal is to enable productive development of high-performance web applications without the developer having to be an expert in browser quirks, XMLHttpRequest, and JavaScript. GWT is used by many products at Google, including AdWords, AdSense, Flights, Hotel Finder, Offers, Wallet, Blogger. It’s open source, completely free, and used by thousands of developers around the world.</p>\r\n<p> </p>\r\n<h3><a href=\"#GetStarted\" id=\"GetStarted\"><i class=\"fa fa-rocket\" style=\"font-size: 23px;\"></i>Get Started</a></h3>\r\n<p>We assume that you already have your <a href=\"http://www.gwtproject.org/gettingstarted.html\">GWT project</a> up and running configured with the <a href=\"http://www.gwtproject.org/doc/latest/RefCommandLineTools.html\"> MAVEN archetype</a>. So the first step is to add the EBI Nexus repository in your pom.xml file:</p>\r\n<pre><code>&lt;repositories&gt;\r\n    ...\r\n    &lt;!-- EBI repo --&gt;\r\n    &lt;repository&gt;\r\n        &lt;id&gt;nexus-ebi-repo&lt;/id&gt;\r\n        &lt;name&gt;The EBI internal repository&lt;/name&gt;\r\n        &lt;url&gt;http://www.ebi.ac.uk/Tools/maven/repos/content/groups/ebi-repo/&lt;/url&gt;\r\n        &lt;releases&gt;\r\n            &lt;enabled&gt;true&lt;/enabled&gt;\r\n        &lt;/releases&gt;\r\n        &lt;snapshots&gt;\r\n            &lt;enabled&gt;false&lt;/enabled&gt;\r\n        &lt;/snapshots&gt;\r\n    &lt;/repository&gt;\r\n    &lt;!-- EBI SNAPSHOT repo --&gt;\r\n    &lt;repository&gt;\r\n        &lt;id&gt;nexus-ebi-snapshot-repo&lt;/id&gt;\r\n        &lt;name&gt;The EBI internal snapshot repository&lt;/name&gt;\r\n        &lt;url&gt;http://www.ebi.ac.uk/Tools/maven/repos/content/groups/ebi-snapshots/&lt;/url&gt;\r\n        &lt;releases&gt;\r\n            &lt;enabled&gt;false&lt;/enabled&gt;\r\n        &lt;/releases&gt;\r\n        &lt;snapshots&gt;\r\n            &lt;enabled&gt;true&lt;/enabled&gt;\r\n        &lt;/snapshots&gt;\r\n    &lt;/repository&gt;\r\n&lt;/repositories&gt;\r\n</code></pre>\r\n<p>Once the repository is in place, the next thing to do is adding the Reactome\'s pathways overview dependency:</p>\r\n<pre><code>&lt;dependencies&gt;\r\n    ...\r\n    &lt;dependency&gt;\r\n        &lt;groupId&gt;org.reactome.web&lt;/groupId&gt;\r\n        &lt;artifactId&gt;fireworks&lt;/artifactId&gt;\r\n        &lt;version&gt;1.0.0&lt;/version&gt;\r\n    &lt;/dependency&gt;\r\n&lt;dependencies&gt;\r\n</code></pre>\r\n<p>Before writing the code to use the pathways overview, add the dependency in your <b>gwt.xml</b> file, so the compiler will find the pathways overview:</p>\r\n<pre><code>&lt;inherits name=\"org.reactome.web.fireworks.FireworksViewer\" /&gt;\r\n</code></pre>\r\n<p>And finally, assuming the widget is now going to be displayed by its own, the code in your <b>EntryPoint</b> class would look like:</p>\r\n<pre><code>/**\r\n * For test purposes we assume the pathway overview loads as a single item in the web page.\r\n *\r\n * Entry point classes define onModuleLoad().\r\n */\r\npublic class Main implements EntryPoint {\r\n\r\n    private final FirewowksViewer fireworks;\r\n\r\n    public Main() {\r\n        //We create the fireworks. Please note that different pathways overview cannot be loaded in the same instance\r\n        fireworks = FireowksFactory.createFireworksViewer();\r\n    }\r\n\r\n    @Override\r\n    public void onModuleLoad() {\r\n        Scheduler.get().scheduleDeferred(new Scheduler.ScheduledCommand() {\r\n            @Override\r\n            public void execute() {\r\n                FireworksFactory.CONSOLE_VERBOSE = true;   //This is optional (for dev purposes)\r\n                FireworksFactory.EVENT_BUS_VERBOSE = true; //This is optional (for dev purposes)\r\n\r\n                //For this use case we add the firewoks as the main and only object of the webpage\r\n                RootLayoutPanel.get().add(fireworks);\r\n            }\r\n        });\r\n    }\r\n}\r\n</code></pre>\r\n<h3><a href=\"#API\" id=\"API\"><i class=\"fa fa-code\" style=\"font-size: 23px;\"></i>API</a></h3>\r\n<p>The available methods are defined in the <b>DiagramViewer</b> interface in the <b>org.reactome.web.diagram.client</b> package. These are:</p>\r\n<pre><code>    //subscribe to this event to be notified when the user closes the analysis overlay\r\n    HandlerRegistration addAnalysisResetHandler(AnalysisResetHandler handler);\r\n\r\n    //Subscribe to this event to be notified if the browser does not support canvas, so diagrams can not be displayed\r\n    HandlerRegistration addCanvasNotSupportedHandler(CanvasNotSupportedHandler handler);\r\n\r\n    //Subscribe to this event to be notified when the overview is displayed in the viewport\r\n    HandlerRegistration addFireworksLoaded(FireworksLoadedHandler handler);\r\n\r\n    //Subscribe to this event to be notified when\r\n    HandlerRegistration addExpressionColumnChangedHandler(ExpressionColumnChangedHandler handler);\r\n\r\n    //Subscribe to this event to be notified when the user hovers over an node in the overview\r\n    HandlerRegistration addNodeHoverHandler(NodeHoverHandler handler);\r\n\r\n    //Subscribe to this event to be notified when the user leave the hovered node in the overview\r\n    HandlerRegistration addNodeHoverResetHandler(NodeHoverResetHandler handler);\r\n\r\n    //Subscribe to this event to be notified when the user opens a node in the overview\r\n    HandlerRegistration addNodeOpenedHandler(NodeOpenedHandler handler);\r\n\r\n    //Subscribe to this event to be notified when the user selects a node in the overview\r\n    HandlerRegistration addNodeSelectedHandler(NodeSelectedHandler handler);\r\n\r\n    //Subscribe to this event to be notified when the user unselects a node in the overview\r\n    HandlerRegistration addNodeSelectedResetHandler(NodeSelectedResetHandler handler);\r\n\r\n    //Subscribe to this event to be notified when the user changes the colour profile\r\n    HandlerRegistration addProfileChangedHandler(ProfileChangedHandler handler);\r\n\r\n    //Returns the selected node\r\n    Node getSelected();\r\n\r\n    //Highlights the node with the provided stable identifier\r\n    void highlightNode(String stableIdentifier);\r\n\r\n    //Highlights the node with the provided stable identifier\r\n    void highlightNode(Long dbIdentifier);\r\n\r\n    //Zooms-in to the node with the provided stable identifier\r\n    void openPathway(String stableIdentifier);\r\n\r\n    //Zooms-in to the node with the provided database identifier\r\n    void openPathway(Long dbIdentifier);\r\n\r\n    //Resets the analysis overlay\r\n    void resetAnalysis();\r\n\r\n    //Resets the highlighted nodes\r\n    void resetHighlight();\r\n\r\n    //Resets the selected nodes\r\n    void resetSelection();\r\n\r\n    //Selects the node with the provided stable identifier\r\n    void selectNode(String stableIdentifier);\r\n\r\n    //Selects the node with the provided database identifier\r\n    void selectNode(Long dbIdentifier);\r\n\r\n    //Sets the analysis overlay for a given \"token\" and \"resource\" (this will query the Analysis Service)\r\n    void setAnalysisToken(String token, String resource);\r\n\r\n    //Show all the nodes in the viewport\r\n    void showAll();</code></pre>\r\n<h3><a href=\"#Resources\" id=\"Resources\"><i class=\"fa fa-book\" style=\"font-size: 23px;\"></i>Resources</a></h3>\r\n<ul style=\"list-style: none;\">\r\n<li style=\"line-height: 30px;\"><a href=\"https://github.com/reactome-pwp/fireworks\"><i class=\"fa fa-github\" style=\"padding-right: 5px;\"></i>GWT Fireworks code</a></li>\r\n<li style=\"line-height: 30px;\"><a href=\"http://github.com/reactome-pwp\"><i class=\"fa fa-github\" style=\"padding-right: 5px;\"></i>Reactome Pathways Portal code</a></li>\r\n</ul>\r\n</div>','',1,29,'2017-01-01 09:00:00',156,'','2017-01-01 09:00:00',156,0,'1000-01-01 00:00:00','2017-06-07 10:16:02','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}',1,5,'','',1,0,'{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}',0,'*','',''),(58,633,'Diagram JS','diagram-js','<style type=\"text/css\">pre, pre code {\r\n    word-wrap: initial;\r\n    white-space: pre;\r\n    word-break: break-all;\r\n}\r\n}</style>\r\n<p>Reactome\'s DiagramJs widget is our diagram viewer in an ordinary JavaScript API. It is meant to be reused by third party resources in order to display Reactome Pathway Diagrams directly in their web pages and enable users to interact with them.</p>\r\n<div id=\"diagramHolder\" class=\"diagram\"></div>\r\n<p> </p>\r\n<h3><a href=\"#reuse-diagram-widget\" id=\"reuse-diagram-widget\"><i class=\"fa fa-rocket\" style=\"font-size: 23px;\"></i>Reusing Reactome\'s Diagram Widget?</a></h3>\r\n<p>To reuse our viewer you need to follow the following steps</p>\r\n<p>1. Include the diagram javascript dependency in you HTML header</p>\r\n<pre><code>&lt;script type=\"text/javascript\" language=\"javascript\" src=\"https://reactome.org/DiagramJs/diagram/diagram.nocache.js\"&gt;&lt;/script&gt;</code></pre>\r\n<p>2. Add a place holder in the body of your web page</p>\r\n<pre><code>&lt;div id=\"diagramHolder\"&gt;&lt;/div&gt;</code></pre>\r\n<p>3. Create and initialise the diagram viewer from your javascript code</p>\r\n<pre><code>    //Creating the Reactome Diagram widget\r\n    //Take into account a proxy needs to be set up in your server side pointing to www.reactome.org\r\n    function onReactomeDiagramReady(){  //This function is automatically called when the widget code is ready to be used\r\n        var diagram = Reactome.Diagram.create({\r\n            \"placeHolder\" : \"diagramHolder\",\r\n            \"width\" : 950, // minimum recommended width\r\n            \"height\" : 500\r\n        });\r\n\r\n        //Initialising it to the \"Hemostasis\" pathway\r\n        diagram.loadDiagram(\"R-HSA-109582\");\r\n\r\n        //Adding different listeners\r\n\r\n        diagram.onDiagramLoaded(function (loaded) {\r\n            console.info(\"Loaded \", loaded);\r\n            diagram.flagItems(\"FYN\");<br /><code><code>            </code>if (loaded == \"R-HSA-109582\") diagram.selectItem(\"R-HSA-109582\");<br /></code><code>        </code>}); <br /><br /><code>        </code>diagram.onObjectHovered(function (hovered){ <br /><code>            </code>console.info(\"Hovered \", hovered); <br /><code>        </code><code></code>}); <br /><br /><code>        </code>diagram.onObjectSelected(function (selected){<br /><code>            </code>console.info(\"Selected \", selected); <br /><code>        </code><code></code>});<br /> <code>    </code>}</code></pre>\r\n<h3><a href=\"#API\" id=\"API\"><i class=\"fa fa-code\" style=\"font-size: 23px;\"></i>DiagramJs API</a></h3>\r\n<p>The current implementation supports the following listeners and methods:</p>\r\n<table class=\"reactome\">\r\n<thead>\r\n<tr><th>Method</th><th>Params</th><th>Description</th></tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td data-label=\"Method\"><b>create</b> :: Constructor<br />   Reactome.Diagram.create(params);</td>\r\n<td data-label=\"Params\"><b>param</b> :: json object<br />  {<br />    \'proxyPrefix\' : string,<br />    \'placeHolder\' : string,<br />    \'width\' : int (optional),<br />    \'height\' : int (optional)<br />  }</td>\r\n<td data-label=\"Description\">Creates and returns a new Reactome.Diagram object</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>loadDiagram(stId)</b> :: void</td>\r\n<td data-label=\"Params\">Pathway stable identifier<br /> <b>stId</b> : string</td>\r\n<td data-label=\"Description\">Loads the specified pathway</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>flagItems(term)</b> :: void</td>\r\n<td data-label=\"Params\">Entity identifier (gene name)<br /> <b>term</b> : string</td>\r\n<td data-label=\"Description\">Flags all entities matching with the \"term\"</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>highlightItem(stId)</b> :: void</td>\r\n<td data-label=\"Params\">Item stable identifier<br /> <b>stId</b> : string</td>\r\n<td data-label=\"Description\">Highlights the specified item if it exists in the diagram</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>resetAnalysis()</b> :: void</td>\r\n<td data-label=\"Params\"> </td>\r\n<td data-label=\"Description\">Resets the analysis overlay</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>resetFlaggedItems()</b> :: void</td>\r\n<td data-label=\"Params\"> </td>\r\n<td data-label=\"Description\">Resets the flagged items</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>resetHighlight()</b> :: void</td>\r\n<td data-label=\"Params\"> </td>\r\n<td data-label=\"Description\">Clears the highlights in the diagram</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>resetSelection()</b> :: void</td>\r\n<td data-label=\"Params\"> </td>\r\n<td data-label=\"Description\">Clears the selection in the diagram</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>resize(width, height)</b> :: void</td>\r\n<td data-label=\"Params\"><b>widht</b>: int<br /> <b>height</b>: int</td>\r\n<td data-label=\"Description\">Resizes the viewport to the specified width and height</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>selectItem(stId)</b> :: void</td>\r\n<td data-label=\"Params\">Item stable identifier<br /> <b>stId</b> : string</td>\r\n<td data-label=\"Description\">Selects the specified item if it exists in the diagram</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>setAnalysisToken(token, resultFilter)</b> :: void</td>\r\n<td data-label=\"Params\">\r\n<p>Analysis token<br /> <b>token</b> : string<br /> Resource<br /><b>resultFilter</b> : <strong>obj</strong></p>\r\n<p><b>obj</b> resultFilter:</p>\r\n<div> {<br />    \'resource\' : string,<br />    \'pValue\' : double,(optional)<br />    \'includeDisease\' : boolean,(optional)<br />    \'min\' : Integer, (optional)</div>\r\n<div>   \'max\':Integer, (optioinal)</div>\r\n<div>   \'speciesList\': [List&lt;String&gt;]<br />  }</div>\r\n</td>\r\n<td data-label=\"Description\">Overlays the analysis result corresponding to the specified (token, resultFilter)</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onObjectSelected(function(obj))</b> :: void</td>\r\n<td data-label=\"Params\"><b>obj</b> is selected item:<br />  {<br />    \'stId\' : string,<br />    \'displayName\' : string,<br />    \'schemaClass\' : string,<br />    \'identifier\' : string (optional),<br />    \'geneNames\' : Array&lt;string&gt; (optional)<br />  }</td>\r\n<td data-label=\"Description\">The function is called when an object in the diagram is selected by the user action</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onObjectHovered(function(obj))</b> :: void</td>\r\n<td data-label=\"Params\"><b>obj</b> is hovered item:<br />  {<br />    \'stId\' : string,<br />    \'displayName\' : string,<br />    \'schemaClass\' : string,<br />    \'identifier\' : string (optional),<br />    \'geneNames\' : Array&lt;string&gt; (optional)<br />  }</td>\r\n<td data-label=\"Description\">The function is called when an object in the diagram is hovered by the user action</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onDiagramLoaded(function(stId))</b> :: void</td>\r\n<td data-label=\"Params\">The stable identifier of the loaded diagram<br /> <b>stId</b>: string</td>\r\n<td data-label=\"Description\">The function is called when a diagram is loaded in the viewer</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onFlagsReset(function())</b> :: void</td>\r\n<td data-label=\"Params\">The function receives no parameters</td>\r\n<td data-label=\"Description\">The function is called when the flagged items have been reset by the user action</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onAnalysisReset(function())</b> :: void</td>\r\n<td data-label=\"Params\">The function receives no parameters</td>\r\n<td data-label=\"Description\">The function is called when the users resets the analysis overlay<br /><br /> diagram.onAnalysisReset(function(){ /* your code here */ });</td>\r\n</tr>\r\n<tr>\r\n<td data-label=\"Method\"><b>onCanvasNotSupported(function())</b> :: void</td>\r\n<td data-label=\"Params\">The function receives no parameters.</td>\r\n<td data-label=\"Description\">The function is called when the browser doesn\'t support HTML5 Canvas so the viewer cannot be instantiated<br /><br /> diagram.onCanvasNotSupported(function(){ /* your code here */ });</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<script type=\"text/javascript\">jQuery(\'base\').remove();\r\nfunction getMainContentWidth(){\r\n   var DIV = \"fav-maincontent\";\r\n   var style = window.getComputedStyle(document.getElementById(DIV), null);\r\n   var width = style.getPropertyValue(\"width\").replace(\"px\",\"\");\r\n   var paddingLeft = style.getPropertyValue(\"padding-left\").replace(\"px\",\"\"); \r\n   var paddingRight = style.getPropertyValue(\"padding-right\").replace(\"px\",\"\");\r\n   return width - paddingLeft - paddingRight;\r\n}\r\njQuery( window ).resize(function() {\r\n  diagram.resize(getMainContentWidth(), 600);  \r\n});\r\nfunction onReactomeDiagramReady() {\r\n        diagram = Reactome.Diagram.create({\r\n            \"placeHolder\": \"diagramHolder\",\r\n            \"width\": getMainContentWidth(),\r\n            \"height\": 600\r\n        });\r\n        diagram.loadDiagram(\"R-HSA-69620\");\r\n        diagram.onDiagramLoaded(function (loaded) {\r\n            diagram.flagItems(\"FYN\");\r\n            if(loaded == \"R-HSA-109582\") diagram.selectItem(\"R-HSA-75892\");\r\n        });\r\n    }\r\n</script>\r\n<script src=\"/DiagramJs/diagram/diagram.nocache.js\" type=\"text/javascript\" language=\"javascript\"></script>','',1,29,'2017-01-01 09:00:00',156,'','2017-01-01 09:00:00',156,0,'1000-01-01 00:00:00','2017-06-07 15:17:14','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}',1,4,'','',1,0,'{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}',0,'*','',''),(59,634,'Diagram GWT','diagram-gwt','<p>Reactome’s Diagram GWT widget is our original implementation of the diagram viewer in <a href=\"http://www.gwtproject.org\" target=\"_blank\" rel=\"nofollow noopener noreferrer\">GWT</a>. It is meant to be reused by third party resources in order to display Reactome Pathway Diagrams directly in their web pages and enable users to interact with them.</p>\r\n<p>{loadposition dev-guide-pd-gwt-1}</p>\r\n<div>\r\n<h3><a href=\"#MoreInfo\" id=\"MoreInfo\"><i class=\"fa fa-question-circle\" style=\"font-size: 23px;\"></i>More Info</a></h3>\r\n<p><a href=\"http://www.gwtproject.org/\">GWT</a>&nbsp;is a development toolkit for building and optimizing complex browser-based applications. Its goal is to enable productive development of high-performance web applications without the developer having to be an expert in browser quirks, XMLHttpRequest, and JavaScript. GWT is used by many products at Google, including AdWords, AdSense, Flights, Hotel Finder, Offers, Wallet, Blogger. It’s open source, completely free, and used by thousands of developers around the world.</p>\r\n<p>&nbsp;</p>\r\n<h3><a href=\"#GetStarted\" id=\"GetStarted\"><i class=\"fa fa-rocket\" style=\"font-size: 23px;\"></i>Get Started</a></h3>\r\n<p>We assume that you already have your <a href=\"http://www.gwtproject.org/gettingstarted.html\">GWT project</a> up and running configured with the <a href=\"http://www.gwtproject.org/doc/latest/RefCommandLineTools.html\"> MAVEN archetype</a>. So the first step is to add the EBI Nexus repository in your pom.xml file:</p>\r\n<pre><code>&lt;repositories&gt;\r\n    ...\r\n    &lt;!-- EBI repo --&gt;\r\n    &lt;repository&gt;\r\n        &lt;id&gt;nexus-ebi-repo&lt;/id&gt;\r\n        &lt;name&gt;The EBI internal repository&lt;/name&gt;\r\n        &lt;url&gt;http://www.ebi.ac.uk/Tools/maven/repos/content/groups/ebi-repo/&lt;/url&gt;\r\n        &lt;releases&gt;\r\n            &lt;enabled&gt;true&lt;/enabled&gt;\r\n        &lt;/releases&gt;\r\n        &lt;snapshots&gt;\r\n            &lt;enabled&gt;false&lt;/enabled&gt;\r\n        &lt;/snapshots&gt;\r\n    &lt;/repository&gt;\r\n    &lt;!-- EBI SNAPSHOT repo --&gt;\r\n    &lt;repository&gt;\r\n        &lt;id&gt;nexus-ebi-snapshot-repo&lt;/id&gt;\r\n        &lt;name&gt;The EBI internal snapshot repository&lt;/name&gt;\r\n        &lt;url&gt;http://www.ebi.ac.uk/Tools/maven/repos/content/groups/ebi-snapshots/&lt;/url&gt;\r\n        &lt;releases&gt;\r\n            &lt;enabled&gt;false&lt;/enabled&gt;\r\n        &lt;/releases&gt;\r\n        &lt;snapshots&gt;\r\n            &lt;enabled&gt;true&lt;/enabled&gt;\r\n        &lt;/snapshots&gt;\r\n    &lt;/repository&gt;\r\n&lt;/repositories&gt;\r\n</code></pre>\r\n<p>Once the repository is in place, the next thing to do is adding the Reactome\'s pathways overview dependency:</p>\r\n<pre><code>&lt;dependencies&gt;\r\n    ...\r\n    &lt;dependency&gt;\r\n        &lt;groupId&gt;org.reactome.web&lt;/groupId&gt;\r\n        &lt;artifactId&gt;diagram&lt;/artifactId&gt;\r\n        &lt;version&gt;3.2.1&lt;/version&gt;\r\n    &lt;/dependency&gt;\r\n&lt;dependencies&gt;\r\n</code></pre>\r\n<p>Before writing the code to use the pathways overview, add the dependency in your <b>gwt.xml</b> file, so the compiler will find the pathways overview:</p>\r\n<pre><code>&lt;inherits name=\"org.reactome.web.diagram.DiagramViewer\" /&gt;\r\n</code></pre>\r\n<p>And finally, assuming the widget is now going to be displayed by its own, the code in your <b>EntryPoint</b> class would look like:</p>\r\n<pre><code>/**\r\n * For test purposes we assume the diagram loads as a single item in the web page.\r\n *\r\n * Entry point classes define onModuleLoad().\r\n */\r\npublic class Main implements EntryPoint {\r\n\r\n    private final DiagramViewer diagram;\r\n\r\n    public Main() {\r\n        //We create the diagram. Please note that different pathways diagrams can be loaded in the same\r\n        //instance (the diagram manages the cache of previously loaded diagrams using an LRU)\r\n        diagram = DiagramFactory.createDiagramViewer();\r\n    }\r\n\r\n    @Override\r\n    public void onModuleLoad() {\r\n        Scheduler.get().scheduleDeferred(new Scheduler.ScheduledCommand() {\r\n            @Override\r\n            public void execute() {\r\n                DiagramFactory.CONSOLE_VERBOSE = true;   //This is optional (for dev purposes)\r\n                DiagramFactory.EVENT_BUS_VERBOSE = true; //This is optional (for dev purposes)\r\n\r\n                //For this use case we add the diagram as the main and only object of the webpage\r\n                RootLayoutPanel.get().add(diagram);\r\n                \r\n                //This will run the loadDiagram method asynchronously, ensuring the DOM has been updated\r\n                Scheduler.get().scheduleDeferred(new Scheduler.ScheduledCommand() {\r\n                    @Override\r\n                    public void execute() {\r\n                        diagram.loadDiagram(\"R-HSA-111804\");\r\n                    }\r\n                });\r\n            }\r\n        });\r\n    }\r\n}</code></pre>\r\n<h3><a href=\"#API\" id=\"API\"><i class=\"fa fa-code\" style=\"font-size: 23px;\"></i>API</a></h3>\r\n<p>The available methods are defined in the <b>DiagramViewer</b> interface in the <b>org.reactome.web.diagram.client</b> package. These are:</p>\r\n<pre><code>    //subscribe to this event to be notified when the user closes the analysis overlay\r\n    HandlerRegistration addAnalysisResetHandler(AnalysisResetHandler handler);\r\n\r\n    //Subscribe to this event to be notified if the browser does not support canvas, so diagrams can not be displayed\r\n    HandlerRegistration addCanvasNotSupportedEventHandler(CanvasNotSupportedHandler handler);\r\n\r\n    //Subscribe to this event to be notified when the user selects an object in the diagram\r\n    HandlerRegistration addDatabaseObjectSelectedHandler(GraphObjectSelectedHandler handler);\r\n\r\n    //Subscribe to this event to be notified when the user hovers over an object in the diagram\r\n    HandlerRegistration addDatabaseObjectHoveredHandler(GraphObjectHoveredHandler handler);\r\n\r\n    //Subscribe to this event to be notified when a new diagram is displayed in the viewport\r\n    HandlerRegistration addDiagramLoadedHandler(DiagramLoadedHandler handler);\r\n\r\n    //Subscribe to this event to be notified when elements in the diagram are flagged\r\n    HandlerRegistration addDiagramObjectsFlaggedHandler(DiagramObjectsFlaggedHandler handler);\r\n\r\n    //Subscribe to this event to be notified when flagged elements in the diagram are not longer visible\r\n    HandlerRegistration addDiagramObjectsFlagResetHandler(DiagramObjectsFlagResetHandler handler);\r\n\r\n    //Subscribe to this event to be notified when the user selects the \"Go to overview\" button\r\n    HandlerRegistration addFireworksOpenedHandler(FireworksOpenedHandler handler);\r\n\r\n    //Flags the elements in the diagram which identifiers match with \"identifier\"\r\n    void flagItems(String identifier);\r\n\r\n    //Highlights the element(s) in the diagram with the \"stableIdentifier\"\r\n    void highlightItem(String stableIdentifier);\r\n\r\n    //Highlights the element(s) in the diagram with the \"dbIdentifier\"\r\n    void highlightItem(Long dbIdentifier);\r\n\r\n    //Loads the diagram with the \"stableIdentifier\"\r\n    void loadDiagram(String stId);\r\n\r\n    //Loads the diagram with the \"dbId\"\r\n    void loadDiagram(Long dbId);\r\n\r\n    //Resets the analysis overlay\r\n    void resetAnalysis();\r\n\r\n    //Resets the flagged items\r\n    void resetFlaggedItems();\r\n\r\n    //Resets the highlighted items\r\n    void resetHighlight();\r\n\r\n    //Resets the selected items\r\n    void resetSelection();\r\n\r\n    //Selects the element(s) in the diagram with the \"stableIdentifier\"\r\n    void selectItem(String stableIdentifier);\r\n\r\n    //Selects the element(s) in the diagram with the \"stableIdentifier\"\r\n    void selectItem(Long dbIdentifier);\r\n\r\n    //Sets the analysis overlay for a given \"token\" and \"resource\" (this will query the Analysis Service)\r\n    void setAnalysisToken(String token, String resource);\r\n\r\n    //Set the widget visibility\r\n    void setVisible(boolean visible);</code></pre>\r\n<h3><a href=\"#Resources\" id=\"Resources\"><i class=\"fa fa-book\" style=\"font-size: 23px;\"></i>Resources</a></h3>\r\n<ul style=\"list-style: none;\">\r\n<li style=\"line-height: 30px;\"><a href=\"http://github.com/reactome-pwp/diagram\"><i class=\"fa fa-github\" style=\"padding-right: 5px;\"></i>GWT Diagram code</a></li>\r\n<li style=\"line-height: 30px;\"><a href=\"http://github.com/reactome-pwp\"><i class=\"fa fa-github\" style=\"padding-right: 5px;\"></i>Reactome Pathways Portal code</a></li>\r\n</ul>\r\n</div>','',1,29,'2017-01-01 09:00:00',156,'','2017-01-01 09:00:00',156,0,'1000-01-01 00:00:00','2017-06-07 15:17:38','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}',1,3,'','',1,0,'{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}',0,'*','',''),(60,638,'Identifiers Mapping','identifiers-mapping','<p>The mapping files consist of a tab-separated table that indicates which external protein, gene or small molecule identifiers in the source database were mapped to Reactome pathway and reaction annotations. Our goal with distributing three sets of files for each different identifier type is to provide these mapping files link the source database identifier to:</p>\r\n<ul>\r\n<li style=\"list-style-type: lower-roman;\">the lowest level pathway diagram or subset of the pathway</li>\r\n<li style=\"list-style-type: lower-roman;\">all level pathway diagrams (filename appended with \'All_Levels’),</li>\r\n<li style=\"list-style-type: lower-roman;\">all reaction events.</li>\r\n</ul>\r\n<p>The columns within the mappings files follow a similar format:</p>\r\n<ol>\r\n<li>Source database identifier, e.g. UniProt, ENSEMBL, NCBI Gene or ChEBI identifier</li>\r\n<li>&nbsp;Reactome Pathway Stable identifier</li>\r\n<li>URL</li>\r\n<li>Event (Pathway or Reaction) Name</li>\r\n<li>Evidence Code</li>\r\n<li>Species</li>\r\n</ol>\r\n<p>If you have any further questions, please <a href=\"mailto:help@reactome.org?subject=Identifier%20Mapping%20File\">contact us</a>.</p>','',1,33,'2017-01-01 09:00:00',156,'','2017-01-01 09:00:00',156,0,'1000-01-01 00:00:00','2017-06-23 09:19:03','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"article_layout\":\"_:default\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}',1,5,'','',1,0,'{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}',0,'*','',''),(61,639,'Pathway Information','pathway-information','<p>The pathway information files consist of tab-separated tables that provide information about Reactome pathway hierarchy:</p>\r\n<ol>\r\n<li>The \"Complete list of pathways\" file maps the Reactome Stable identifier (ST_ID) to a pathway name and corresponding species.</li>\r\n<li>The \"Pathway hierarchy relationship\" file consists of two columns of Reactome Stable identifiers (ST_ID), defining the relationship between pathways within the pathway hierarchy. The first column provides the parent pathway stable identifier, whereas the second column provides the child pathway stable identifier.</li>\r\n</ol>','',1,2,'2017-01-01 09:00:00',156,'','2017-01-01 09:00:00',156,0,'1000-01-01 00:00:00','2017-06-23 09:37:27','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}',1,11,'','',1,0,'{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}',0,'*','',''),(62,640,'Tab-Delimiter Format','tab-delimiter-format','<p>Interactions are generated electronically based on the data that we store on complexes and reactions, i.e. the interactions provided by Reactome are not curated and are not experimental data. In addition, the complexes and reactions in species other than human are derived by orthology inference from the correspondinghuman complexes and reactions.</p>\r\n<p>The meaning of \"interaction\" is quite broad: 2 protein sequences occur in the same complex or they occur in the same reaction.</p>\r\n<p>The 1st line begins with \'#\' and gives the number of unique interactions, i.e. unique pairs if UniProt accession numbers.</p>\r\n<p>The rest of the file is tab delimited with columns having following meaning/content:</p>\r\n<ul style=\"list-style: decimal;\">\r\n<li>Protein ID (e.g. UniProt accession) for interactor 1.</li>\r\n<li>Ensembl gene id(s delimited by \'|\') for interactor 1. Note that one UniProt entry may be mapped to multiple Ensembl genes.</li>\r\n<li>Entrez Gene id(s delimited by \'|\') for interactor 1. Note that one UniProt entry may be mapped to multiple Entrez Gene ids.</li>\r\n<li>Protein ID (e.g. UniProt accession) for interactor 2.</li>\r\n<li>Ensembl gene id(s delimited by \'|\') for interactor 2. Note that one UniProt entry may be mapped to multiple Ensembl genes.</li>\r\n<li>Entrez Gene id(s delimited by \'|\') for interactor 2. Note that one UniProt entry may be mapped to multiple Entrez Gene ids.</li>\r\n<li>Type of interaction, either \'direct_complex\', \'indirect_complex\', \'reaction\' or \'neighbouring_reaction\'. Meanings of those below.</li>\r\n<li>Interaction context. This is the class and internal identifier of Reactome instance via which this interaction happens. For \'neighbouring_reaction\' type interaction this contains a pair of class:identifier string separated by \'\'.</li>\r\n<li>Comma-delimited list of PubMed identifiers supporting this interaction. For a reaction these are the PMIDs from the Reaction.literatureReference slot (and not from the \"second tier\" Reaction.summation.literatureReference). For a complex these are from reations which produce the complex. For \'neighbouring_reaction\' type interaction this field is empty.</li>\r\n</ul>\r\n<h3>Interaction types</h3>\r\n<p>In Reactome, complexes can be arbitrarily nested, i.e., contain subcomplexes. Here we have complex C1 consisting of complexes C2 and C3. C2 consist of molecules A and B. C3 consists of molecules C and D:</p>\r\n<pre>           C1 --- C2 --- A\r\n                |      |\r\n                |      |- B\r\n                |\r\n                |- C3 --- C\r\n                |\r\n                |- D\r\n                </pre>\r\n<p>Now really to the interaction types:</p>\r\n<ul>\r\n<li>direct_complex - interactors are directly in the same complex, i.e. w/o further nested complexes. From the example interactions A B and C are of this type.</li>\r\n<li>indirect_complex - interactors in different subcomplexes of a complex. From the example above interactions AC, AD, BC and BD are of this type.</li>\r\n<li>reaction - interactors participate in the same reaction. Only those reactions are reported for which the intreactors are not complexed (with the exception being association dissociation reactions which are reported).</li>\r\n<li>neighbouring_reaction - interactor participate in 2 \"consecutive\" reactions, i.e. when one reaction produces a PhysicalEntity which is either an input or a catalyst for another reaction. However, to avoid the \"trivial\" (i.e. over ATP, ADP etc) interactions, the computation is done using the \'precedingEvent\' attribute used to order reactions in a pathway.</li>\r\n</ul>\r\n<p>The \'direct_complex\' type doesn\'t really mean true pairwise interaction. For example, Reactome\'s representation of ribosomal S60 subunit consists \"directly\" of 40+ proteins and has no sub-complexes. Obviously this can hardly mean that each one of those component proteins directly interacts with every other protein. Hence, the \'direct_complex\' vs. \'indirect_complex\' distinction is quite meaningless.</p>\r\n<h3>Other notes:</h3>\r\n<ul>\r\n<li>If the same interaction (i.e same UniProte accession pair) happens in multiple contexts, each context is reported on a separate row and hence the row number in the file is bigger than the number of unique interactions reported on the 1st line.</li>\r\n<li>The order of interactors on one line has no meaning (they are just ordered according to the Reactome internal identifier...). Every interaction with context is reported once, i.e. AB is not reported as BA.</li>\r\n</ul>','',1,33,'2017-01-01 09:00:00',156,'','2017-01-01 09:00:00',156,0,'1000-01-01 00:00:00','2017-06-23 10:02:36','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"article_layout\":\"_:default\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}',1,6,'','',1,0,'{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}',0,'*','',''),(63,641,'Curator Tool (version 4.0.3, build 118, March 3, 2024)','reactome-curator-tool-3','<p></p>\r\n<style type=\"text/css\">ul{ list-style-type: square; }</style>\r\n<p></p>\r\n<div id=\"top\"></div>\r\n<center>\r\n<p><span><b>Available Installers</b></span></p>\r\n<table border=\"0\" cellspacing=\"10\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td> </td>\r\n<td><span>Platform</span></td>\r\n<td> </td>\r\n<td> </td>\r\n<td><center><span>Installer</span></center></td>\r\n<td> </td>\r\n<td><center><span>Instructions</span></center></td>\r\n</tr>\r\n<tr>\r\n<td style=\"text-align: center;\" valign=\"center\"><i class=\"fa fa-windows\"></i></td>\r\n<td valign=\"bottom\">\r\n<div style=\"text-align: left;\"><span>Windows</span></div>\r\n</td>\r\n<td valign=\"bottom\"> </td>\r\n<td valign=\"bottom\"> </td>\r\n<td valign=\"bottom\"><center><a href=\"https://reactome.org/download/tools/curatorTool/InstData/win_linux/install.zip\">Download (15.4M)</a></center></td>\r\n<td valign=\"bottom\"> </td>\r\n<td valign=\"bottom\"><center><a href=\"#windows\">View</a></center></td>\r\n</tr>\r\n<tr>\r\n<td style=\"text-align: center;\" valign=\"center\"><i class=\"fa fa-apple\"></i></td>\r\n<td valign=\"bottom\">\r\n<div style=\"text-align: left;\"><span>Mac OS X</span></div>\r\n</td>\r\n<td valign=\"bottom\"> </td>\r\n<td valign=\"bottom\"> </td>\r\n<td valign=\"bottom\"><center><a href=\"https://reactome.org/download/tools/curatorTool/InstData/mac/install.zip\">Download (15.4)</a></center></td>\r\n<td valign=\"bottom\"> </td>\r\n<td valign=\"bottom\"><center><a href=\"#macosx\">View</a></center></td>\r\n</tr>\r\n<tr>\r\n<td style=\"text-align: center;\" valign=\"center\"><i class=\"fa fa-linux\"></i></td>\r\n<td valign=\"bottom\">\r\n<div style=\"text-align: left;\"><span>Linux</span></div>\r\n</td>\r\n<td valign=\"bottom\"> </td>\r\n<td valign=\"bottom\"> </td>\r\n<td valign=\"bottom\"><center><a href=\"https://reactome.org/download/tools/curatorTool/InstData/win_linux/install.zip\">Download (15.4M)</a></center></td>\r\n<td valign=\"bottom\"> </td>\r\n<td valign=\"bottom\"><center><a href=\"#linux\">View</a></center></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</center><hr />\r\n<div id=\"windows\" style=\"text-align: left;\">\r\n<h4><a href=\"#windows\" name=\"windows\">Windows:</a></h4>\r\n<h5>Instructions</h5>\r\n<ul>\r\n<li>After downloading, double-click <b><code>install.zip</code></b> to unzip it to an empty folder. This folder should be named \"ReactomeCuratorTool\" though any name can be used. Please use the latest version of WinZip if you use this application for unzipping.</li>\r\n<li>After unzipping, an application called ReactomeCuratorTool.exe should be found in the named folder. Double click this application to launch the Reactome Curator Tool.</li>\r\n</ul>\r\n<h5>Notes</h5>\r\n<ul>\r\n<li>You need to install Java 11. You can download one from <a href=\"https://adoptium.net/temurin/releases/?version=11\">OpenJDK</a>.</li>\r\n</ul>\r\n<p>(<a href=\"#top\">Go To Top</a>)</p>\r\n</div>\r\n<center><hr /></center>\r\n<div id=\"macosx\" style=\"text-align: left;\">\r\n<h4><a href=\"#macosx\" name=\"macosx\">Mac OS X:</a></h4>\r\n<h5>Instructions</h5>\r\n<ul>\r\n<li>Please refer this document for updated information: <a href=\"https://devwiki.reactome.org/index.php/New_Curators_Guide#Downloading.2C_Installing.2C_And_Maintaining_The_Curator_Tool\">https://devwiki.reactome.org/index.php/New_Curators_Guide#Downloading.2C_Installing.2C_And_Maintaining_The_Curator_Tool</a></li>\r\n<li>After downloading, double-click <b><code>install.zip</code></b> to unzip the folder.</li>\r\n<li>Follow the instruction for the linux version below.</li>\r\n</ul>\r\n<h5>Notes</h5>\r\n<ul>\r\n<li>Requires Mac OS X 10.4.8 or later, and Java 11.</li>\r\n</ul>\r\n<p>(<a href=\"#top\">Go To Top</a>)</p>\r\n</div>\r\n<center><hr /></center>\r\n<div id=\"linux\" style=\"text-align: left;\">\r\n<h4><a href=\"#linux\" name=\"linux\">Linux:</a></h4>\r\n<h5>Instructions</h5>\r\n<ul>\r\n<li>After downloading open a shell, and <span><b><code>cd</code></b></span><span> to the directory where you downloaded the installer.</span></li>\r\n<li>At the prompt, type <span><b><code>unzip install.zip</code></b> to unzip the installer.</span></li>\r\n<li>Use ./ReactomeCuratorTool.sh to launch the Reactome Curator Tool.</li>\r\n</ul>\r\n<h5>Notes</h5>\r\n<ul>\r\n<li><span>You need to install Java 11. You can download one from <a href=\"https://adoptium.net/temurin/releases/?version=11\">OpenJDK</a>.</span></li>\r\n</ul>\r\n<p>(<a href=\"#top\">Go To Top</a>)</p>\r\n</div>','',1,14,'2017-01-01 09:00:00',156,'','2017-01-01 09:00:00',156,0,'1000-01-01 00:00:00','2017-06-23 10:53:19','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}',1,9,'','',1,0,'{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}',0,'*','',''),(64,668,'Developer\'s Zone','developer-s-zone','<h4>Explore our tools and web services and learn how to include them in your applications</h4>\r\n<p>{loadposition dev-guide-1}{loadposition dev-guide-2}</p>','',1,2,'2017-01-01 09:00:00',156,'','2017-01-01 09:00:00',156,0,'1000-01-01 00:00:00','2017-06-29 11:12:14','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_cap